     1/* eslint-disable lingui/no-unlocalized-strings */
     2import { useState, useEffect, useCallback, useRef } from "react";
     3import { FormattedNumber } from "@/components/formatted-number/formatted-number";
     4import { Bot, AlertTriangle, TrendingUp, TrendingDown, Activity, Zap, Target, ShieldAlert, Clock, Eye, Flame, Wallet, Settings, RefreshCw, Power, Layers, CheckCircle2, Circle } from "lucide-react";
     5import { cn } from "@/lib/utils";
     6import { Switch } from "@/components/switch";
     7import { Tooltip } from "@/components/tooltip";
     8
     9// Types matching the backend WebSocket events
    10interface PositionData {
    11  mint: string;
    12  symbol: string;
    13  action: "buy" | "sell";
    14  entryPriceSol: number;
    15  currentPriceSol: number;
    16  positionSizeSol: number;
    17  pnlPercent: number;
    18  pnlSol: number;
    19  entryTime: number;
    20}
    21
    22interface SignalData {
    23  timestamp: number;
    24  mint: string;
    25  symbol: string;
    26  signalType: "magic_buy" | "magic_exit" | "dump_exit" | "rule_signal";
    27  action: "buy" | "sell" | "close";
    28  reason: string;
    29  pnlPercent?: number;
    30  entryType?: string;
    31}
    32
    33interface MarketStatusData {
    34  state: "NORMAL" | "CAUTION" | "DUMP";
    35  solPriceChangePercent: number;
    36  blockEntries: boolean;
    37  exitAll: boolean;
    38}
    39
    40interface StatsData {
    41  totalTrades: number;
    42  openTrades: number;
    43  closedTrades: number;
    44  wins: number;
    45  losses: number;
    46  winRate: number;
    47  totalPnlSol: number;
    48  totalPnlUsd: number;
    49  avgPnlPercent: number;
    50}
    51
    52interface ScanTickData {
    53  timestamp: number;
    54  scanNumber: number;
    55  tokenCount: number;
    56  openPositions: number;
    57}
    58
    59interface WatchedToken {
    60  mint: string;
    61  symbol: string;
    62  source: "watchlist" | "trending" | "whales";
    63  priceSol?: number;
    64  priceUsd?: number;
    65  change1h?: number;
    66  change24h?: number;
    67  volume24h?: number;
    68  mcap?: number;
    69  hasPosition?: boolean;
    70}
    71
    72interface TokenListData {
    73  watchlist: WatchedToken[];
    74  trending: WatchedToken[];
    75  whales: WatchedToken[];
    76}
    77
    78interface ConfigData {
    79  autoSourcesEnabled: boolean;
    80  trendingEnabled: boolean;
    81  whalesEnabled: boolean;
    82  marketFilterEnabled: boolean;
    83  mode: "paper" | "live";
    84  positionSizeSol: number;
    85  pollIntervalMs: number;
    86}
    87
    88interface StrategyInfo {
    89  id: string;
    90  name: string;
    91  description: string;
    92  mode: "all" | "any";
    93  ruleCount: number;
    94  summary: string;
    95}
    96
    97interface StrategiesData {
    98  available: StrategyInfo[];
    99  active: string[];
   100}
   101
   102type AutopilotCommand =
   103  | { command: "toggle_sources"; enabled: boolean }
   104  | { command: "toggle_trending"; enabled: boolean }
   105  | { command: "toggle_whales"; enabled: boolean }
   106  | { command: "toggle_filter"; enabled: boolean }
   107  | { command: "set_mode"; mode: "paper" | "live" }
   108  | { command: "set_position_size"; sizeSol: number }
   109  | { command: "refresh_sources" }
   110  | { command: "get_config" }
   111  | { command: "get_strategies" }
   112  | { command: "toggle_strategy"; strategyId: string; enabled: boolean };
   113
   114type AutopilotEvent =
   115  | { type: "position_update"; positions: PositionData[] }
   116  | { type: "signal"; signal: SignalData }
   117  | { type: "market_status"; status: MarketStatusData }
   118  | { type: "stats_update"; stats: StatsData }
   119  | { type: "scan_tick"; tick: ScanTickData }
   120  | { type: "token_list"; tokens: TokenListData }
   121  | { type: "config"; config: ConfigData }
   122  | { type: "strategies"; strategies: StrategiesData };
   123
   124// Determine WebSocket URL based on protocol (use proxy for HTTPS)
   125const getAutopilotWsUrl = () => {
   126  if (typeof window === "undefined") return "ws://localhost:8765";
   127  // Use vite proxy when on HTTPS to avoid mixed content
   128  if (window.location.protocol === "https:") {
   129    return `wss://${window.location.host}/autopilot-ws`;
   130  }
   131  return "ws://localhost:8765";
   132};
   133
   134// WebSocket hook for autopilot backend
   135function useAutopilotWebSocket(wsUrl: string = getAutopilotWsUrl()) {
   136  const [positions, setPositions] = useState<PositionData[]>([]);
   137  const [signals, setSignals] = useState<SignalData[]>([]);
   138  const [marketStatus, setMarketStatus] = useState<MarketStatusData>({
   139    state: "NORMAL",
   140    solPriceChangePercent: 0,
   141    blockEntries: false,
   142    exitAll: false,
   143  });
   144  const [stats, setStats] = useState<StatsData>({
   145    totalTrades: 0,
   146    openTrades: 0,
   147    closedTrades: 0,
   148    wins: 0,
   149    losses: 0,
   150    winRate: 0,
   151    totalPnlSol: 0,
   152    totalPnlUsd: 0,
   153    avgPnlPercent: 0,
   154  });
   155  const [scanTick, setScanTick] = useState<ScanTickData | null>(null);
   156  const [tokenList, setTokenList] = useState<TokenListData>({
   157    watchlist: [],
   158    trending: [],
   159    whales: [],
   160  });
   161  const [config, setConfig] = useState<ConfigData>({
   162    autoSourcesEnabled: true,
   163    trendingEnabled: true,
   164    whalesEnabled: true,
   165    marketFilterEnabled: true,
   166    mode: "paper",
   167    positionSizeSol: 0.1,
   168    pollIntervalMs: 30000,
   169  });
   170  const [strategies, setStrategies] = useState<StrategiesData>({
   171    available: [],
   172    active: [],
   173  });
   174  const [isConnected, setIsConnected] = useState(false);
   175  const wsRef = useRef<WebSocket | null>(null);
   176  const reconnectTimeoutRef = useRef<ReturnType<typeof setTimeout> | null>(null);
   177
   178  const sendCommand = useCallback((command: AutopilotCommand) => {
   179    if (wsRef.current?.readyState === WebSocket.OPEN) {
   180      wsRef.current.send(JSON.stringify(command));
   181    }
   182  }, []);
   183
   184  const connect = useCallback(() => {
   185    if (wsRef.current?.readyState === WebSocket.OPEN) return;
   186
   187    try {
   188      const ws = new WebSocket(wsUrl);
   189      wsRef.current = ws;
   190
   191      ws.onopen = () => {
   192        setIsConnected(true);
   193        console.log("[Autopilot] WebSocket connected");
   194      };
   195
   196      ws.onclose = () => {
   197        setIsConnected(false);
   198        console.log("[Autopilot] WebSocket disconnected, reconnecting...");
   199        // Reconnect after 3 seconds
   200        reconnectTimeoutRef.current = setTimeout(connect, 3000);
   201      };
   202
   203      ws.onerror = (err) => {
   204        console.error("[Autopilot] WebSocket error:", err);
   205      };
   206
   207      ws.onmessage = (event) => {
   208        try {
   209          const data = JSON.parse(event.data) as AutopilotEvent;
   210
   211          switch (data.type) {
   212            case "position_update":
   213              console.log("[Autopilot] Positions received:", data.positions.length, data.positions);
   214              setPositions(data.positions);
   215              break;
   216            case "signal":
   217              console.log("[Autopilot] Signal received:", data.signal);
   218              setSignals((prev) => [data.signal, ...prev].slice(0, 50)); // Keep last 50 signals
   219              break;
   220            case "market_status":
   221              console.log("[Autopilot] Market status:", data.status.state, data.status.solPriceChangePercent + "%");
   222              setMarketStatus(data.status);
   223              break;
   224            case "stats_update":
   225              console.log("[Autopilot] Stats update:", data.stats);
   226              setStats(data.stats);
   227              break;
   228            case "scan_tick":
   229              console.log("[Autopilot] Scan tick #" + data.tick.scanNumber, data.tick.tokenCount + " tokens");
   230              setScanTick(data.tick);
   231              break;
   232            case "token_list":
   233              console.log("[Autopilot] Token list:", data.tokens.watchlist.length + "W", data.tokens.trending.length + "T", data.tokens.whales.length + "$");
   234              setTokenList(data.tokens);
   235              break;
   236            case "config":
   237              console.log("[Autopilot] Config received:", data.config);
   238              setConfig(data.config);
   239              break;
   240            case "strategies":
   241              console.log("[Autopilot] Strategies received:", data.strategies.available.length, "available,", data.strategies.active.length, "active");
   242              setStrategies(data.strategies);
   243              break;
   244          }
   245        } catch (err) {
   246          console.error("[Autopilot] Failed to parse message:", err);
   247        }
   248      };
   249    } catch (err) {
   250      console.error("[Autopilot] Failed to connect:", err);
   251      reconnectTimeoutRef.current = setTimeout(connect, 3000);
   252    }
   253  }, [wsUrl]);
   254
   255  useEffect(() => {
   256    connect();
   257    return () => {
   258      if (reconnectTimeoutRef.current) {
   259        clearTimeout(reconnectTimeoutRef.current);
   260      }
   261      wsRef.current?.close();
   262    };
   263  }, [connect]);
   264
   265  return { positions, signals, marketStatus, stats, scanTick, tokenList, config, strategies, isConnected, sendCommand };
   266}
   267
   268// Market Status Badge Component
   269const MarketStatusBadge = ({ status }: { status: MarketStatusData }) => {
   270  const stateConfig = {
   271    NORMAL: { color: "bg-green-500/20 text-green-400 border-green-500/30", icon: Activity, label: "NORMAL" },
   272    CAUTION: { color: "bg-yellow-500/20 text-yellow-400 border-yellow-500/30", icon: AlertTriangle, label: "CAUTION" },
   273    DUMP: { color: "bg-red-500/20 text-red-400 border-red-500/30", icon: ShieldAlert, label: "DUMP" },
   274  };
   275
   276  const config = stateConfig[status.state];
   277  const Icon = config.icon;
   278
   279  return (
   280    <div className={cn("flex items-center gap-2 rounded-lg border px-3 py-1.5", config.color)}>
   281      <Icon className="h-4 w-4" />
   282      <span className="text-xs font-semibold">{config.label}</span>
   283      <span className="text-xs opacity-75">
   284        SOL {status.solPriceChangePercent >= 0 ? "+" : ""}
   285        {status.solPriceChangePercent.toFixed(2)}%
   286      </span>
   287    </div>
   288  );
   289};
   290
   291// Position Card Component
   292const PositionCard = ({ position }: { position: PositionData }) => {
   293  const isProfit = position.pnlPercent >= 0;
   294  const holdTime = Math.floor((Date.now() - position.entryTime) / 60000); // minutes
   295
   296  return (
   297    <div className="border-border-primary bg-bg-tertiary/50 flex items-center justify-between rounded-lg border p-3">
   298      <div className="flex items-center gap-3">
   299        <div
   300          className={cn(
   301            "flex h-8 w-8 items-center justify-center rounded-lg text-xs font-bold",
   302            isProfit ? "bg-green-500/20 text-green-400" : "bg-red-500/20 text-red-400"
   303          )}
   304        >
   305          {isProfit ? <TrendingUp className="h-4 w-4" /> : <TrendingDown className="h-4 w-4" />}
   306        </div>
   307        <div>
   308          <span className="text-text-primary text-sm font-medium">${position.symbol}</span>
   309          <div className="text-text-secondary flex items-center gap-1 text-[10px]">
   310            <Clock className="h-3 w-3" />
   311            {holdTime}m
   312          </div>
   313        </div>
   314      </div>
   315      <div className="text-right">
   316        <div className={cn("text-sm font-semibold", isProfit ? "text-green-400" : "text-red-400")}>
   317          {isProfit ? "+" : ""}
   318          {position.pnlPercent.toFixed(2)}%
   319        </div>
   320        <div className="text-text-secondary text-[10px]">
   321          {position.positionSizeSol.toFixed(2)} SOL
   322        </div>
   323      </div>
   324    </div>
   325  );
   326};
   327
   328// Signal Item Component
   329const SignalItem = ({ signal }: { signal: SignalData }) => {
   330  const signalConfig = {
   331    magic_buy: { color: "text-cyan-400", icon: Target, label: "BUY" },
   332    magic_exit: { color: "text-purple-400", icon: Zap, label: "EXIT" },
   333    dump_exit: { color: "text-red-400", icon: ShieldAlert, label: "DUMP" },
   334    rule_signal: { color: "text-blue-400", icon: Activity, label: "SIGNAL" },
   335  };
   336
   337  const config = signalConfig[signal.signalType];
   338  const Icon = config.icon;
   339  const time = new Date(signal.timestamp).toLocaleTimeString("en-US", { hour12: false, hour: "2-digit", minute: "2-digit", second: "2-digit" });
   340
   341  return (
   342    <div className="border-border-primary/50 flex items-center gap-2 border-b py-2 last:border-0">
   343      <Icon className={cn("h-3.5 w-3.5", config.color)} />
   344      <span className="text-text-secondary text-[10px]">{time}</span>
   345      <span className={cn("text-xs font-medium", config.color)}>{config.label}</span>
   346      <span className="text-text-primary text-xs">${signal.symbol}</span>
   347      {signal.pnlPercent !== undefined && (
   348        <span className={cn("text-xs", signal.pnlPercent >= 0 ? "text-green-400" : "text-red-400")}>
   349          {signal.pnlPercent >= 0 ? "+" : ""}
   350          {signal.pnlPercent.toFixed(1)}%
   351        </span>
   352      )}
   353    </div>
   354  );
   355};
   356
   357// Token Row Component
   358const TokenRow = ({ token }: { token: WatchedToken }) => {
   359  const change = token.change1h ?? 0;
   360  const isPositive = change >= 0;
   361
   362  return (
   363    <div className={cn(
   364      "flex items-center justify-between rounded px-2 py-1.5 text-xs",
   365      token.hasPosition ? "bg-cyan-500/10 border border-cyan-500/20" : "bg-bg-tertiary/30"
   366    )}>
   367      <div className="flex items-center gap-2">
   368        <span className="text-text-primary font-medium">${token.symbol}</span>
   369        {token.hasPosition && (
   370          <span className="text-[8px] text-cyan-400 font-semibold">OPEN</span>
   371        )}
   372      </div>
   373      <div className="flex items-center gap-3">
   374        {token.priceUsd !== undefined && (
   375          <span className="text-text-secondary">
   376            ${token.priceUsd < 0.01 ? token.priceUsd.toExponential(2) : token.priceUsd.toFixed(4)}
   377          </span>
   378        )}
   379        <span className={cn("font-medium", isPositive ? "text-green-400" : "text-red-400")}>
   380          {isPositive ? "+" : ""}{change.toFixed(1)}%
   381        </span>
   382      </div>
   383    </div>
   384  );
   385};
