   387// Token List Section Component
   388const TokenListSection = ({
   389  title,
   390  icon: Icon,
   391  tokens,
   392  iconColor
   393}: {
   394  title: string;
   395  icon: React.ElementType;
   396  tokens: WatchedToken[];
   397  iconColor: string;
   398}) => {
   399  if (tokens.length === 0) return null;
   400
   401  return (
   402    <div className="flex-shrink-0">
   403      <div className="text-text-secondary mb-1.5 flex items-center gap-2 text-xs font-medium">
   404        <Icon className={cn("h-3.5 w-3.5", iconColor)} />
   405        {title} ({tokens.length})
   406      </div>
   407      <div className="max-h-32 space-y-1 overflow-auto">
   408        {tokens.map((token) => (
   409          <TokenRow key={token.mint} token={token} />
   410        ))}
   411      </div>
   412    </div>
   413  );
   414};
   415
   416// Controls Panel Component
   417const ControlsPanel = ({
   418  config,
   419  sendCommand,
   420  isConnected,
   421}: {
   422  config: ConfigData;
   423  sendCommand: (cmd: AutopilotCommand) => void;
   424  isConnected: boolean;
   425}) => {
   426  const [showControls, setShowControls] = useState(false);
   427
   428  return (
   429    <div className="border-border-primary bg-bg-tertiary/30 rounded-lg border">
   430      {/* Header - always visible */}
   431      <button
   432        onClick={() => setShowControls(!showControls)}
   433        className="flex w-full items-center justify-between px-3 py-2 text-xs"
   434        disabled={!isConnected}
   435      >
   436        <div className="flex items-center gap-2">
   437          <Settings className="h-3.5 w-3.5 text-text-secondary" />
   438          <span className="text-text-secondary font-medium">Controls</span>
   439        </div>
   440        <div className="flex items-center gap-2">
   441          <span className={cn(
   442            "rounded px-1.5 py-0.5 text-[10px] font-semibold",
   443            config.mode === "paper" ? "bg-yellow-500/20 text-yellow-400" : "bg-green-500/20 text-green-400"
   444          )}>
   445            {config.mode.toUpperCase()}
   446          </span>
   447          <span className="text-text-tertiary">{showControls ? "" : ""}</span>
   448        </div>
   449      </button>
   450
   451      {/* Expandable controls */}
   452      {showControls && (
   453        <div className="border-t border-border-primary/50 px-3 py-2 space-y-3">
   454          {/* Mode Toggle - Disabled until LOIS integration */}
   455          <Tooltip
   456            content="Live trading integration coming soon. Invite only "
   457            side="left"
   458            triggerClassName="w-full"
   459          >
   460            <div className="flex w-full items-center justify-between opacity-50 cursor-not-allowed">
   461              <div className="flex items-center gap-2">
   462                <Power className="h-3.5 w-3.5 text-text-secondary" />
   463                <span className="text-xs text-text-secondary">Live Trading</span>
   464              </div>
   465              <Switch
   466                size="sm"
   467                checked={false}
   468                disabled
   469              />
   470            </div>
   471          </Tooltip>
   472
   473          {/* Auto Sources Toggle */}
   474          <div className="flex items-center justify-between">
   475            <div className="flex items-center gap-2">
   476              <Eye className="h-3.5 w-3.5 text-blue-400" />
   477              <span className="text-xs text-text-secondary">Auto Sources</span>
   478            </div>
   479            <Switch
   480              size="sm"
   481              checked={config.autoSourcesEnabled}
   482              onCheckedChange={(checked) => sendCommand({ command: "toggle_sources", enabled: checked })}
   483            />
   484          </div>
   485
   486          {/* Trending Toggle */}
   487          {config.autoSourcesEnabled && (
   488            <div className="flex items-center justify-between pl-5">
   489              <div className="flex items-center gap-2">
   490                <Flame className="h-3 w-3 text-orange-400" />
   491                <span className="text-[11px] text-text-tertiary">Trending</span>
   492              </div>
   493              <Switch
   494                size="sm"
   495                checked={config.trendingEnabled}
   496                onCheckedChange={(checked) => sendCommand({ command: "toggle_trending", enabled: checked })}
   497              />
   498            </div>
   499          )}
   500
   501          {/* Whales Toggle */}
   502          {config.autoSourcesEnabled && (
   503            <div className="flex items-center justify-between pl-5">
   504              <div className="flex items-center gap-2">
   505                <Wallet className="h-3 w-3 text-green-400" />
   506                <span className="text-[11px] text-text-tertiary">Whale Moves</span>
   507              </div>
   508              <Switch
   509                size="sm"
   510                checked={config.whalesEnabled}
   511                onCheckedChange={(checked) => sendCommand({ command: "toggle_whales", enabled: checked })}
   512              />
   513            </div>
   514          )}
   515
   516          {/* Market Filter Toggle */}
   517          <div className="flex items-center justify-between">
   518            <div className="flex items-center gap-2">
   519              <ShieldAlert className="h-3.5 w-3.5 text-purple-400" />
   520              <span className="text-xs text-text-secondary">Market Filter</span>
   521            </div>
   522            <Switch
   523              size="sm"
   524              checked={config.marketFilterEnabled}
   525              onCheckedChange={(checked) => sendCommand({ command: "toggle_filter", enabled: checked })}
   526            />
   527          </div>
   528
   529          {/* Refresh Sources Button */}
   530          <button
   531            onClick={() => sendCommand({ command: "refresh_sources" })}
   532            className="flex w-full items-center justify-center gap-2 rounded bg-bg-tertiary/50 px-3 py-1.5 text-xs text-text-secondary hover:bg-bg-tertiary transition-colors"
   533          >
   534            <RefreshCw className="h-3 w-3" />
   535            Refresh Sources
   536          </button>
   537
   538          {/* Position Size Display */}
   539          <div className="flex items-center justify-between text-[10px] text-text-tertiary pt-1 border-t border-border-primary/30">
   540            <span>Position Size</span>
   541            <span>{config.positionSizeSol} SOL</span>
   542          </div>
   543        </div>
   544      )}
   545    </div>
   546  );
   547};
   548
   549// Strategies Panel Component
   550const StrategiesPanel = ({
   551  strategies,
   552  sendCommand,
   553  isConnected,
   554}: {
   555  strategies: StrategiesData;
   556  sendCommand: (cmd: AutopilotCommand) => void;
   557  isConnected: boolean;
   558}) => {
   559  const [showStrategies, setShowStrategies] = useState(false);
   560  const activeSet = new Set(strategies.active);
   561
   562  return (
   563    <div className="border-border-primary bg-bg-tertiary/30 rounded-lg border">
   564      {/* Header - always visible */}
   565      <button
   566        onClick={() => setShowStrategies(!showStrategies)}
   567        className="flex w-full items-center justify-between px-3 py-2 text-xs"
   568        disabled={!isConnected}
   569      >
   570        <div className="flex items-center gap-2">
   571          <Layers className="h-3.5 w-3.5 text-text-secondary" />
   572          <span className="text-text-secondary font-medium">Strategies</span>
   573        </div>
   574        <div className="flex items-center gap-2">
   575          <span className={cn(
   576            "rounded px-1.5 py-0.5 text-[10px] font-semibold",
   577            strategies.active.length > 0 ? "bg-green-500/20 text-green-400" : "bg-gray-500/20 text-gray-400"
   578          )}>
   579            {strategies.active.length} ACTIVE
   580          </span>
   581          <span className="text-text-tertiary">{showStrategies ? "" : ""}</span>
   582        </div>
   583      </button>
   584
   585      {/* Expandable strategies list */}
   586      {showStrategies && (
   587        <div className="border-t border-border-primary/50 px-3 py-2 space-y-2">
   588          {strategies.available.length === 0 ? (
   589            <div className="text-text-tertiary text-xs text-center py-2">
   590              No strategies available
   591            </div>
   592          ) : (
   593            strategies.available.map((strategy) => {
   594              const isActive = activeSet.has(strategy.id);
   595              return (
   596                <button
   597                  key={strategy.id}
   598                  onClick={() => sendCommand({ command: "toggle_strategy", strategyId: strategy.id, enabled: !isActive })}
   599                  className={cn(
   600                    "w-full flex items-start gap-2 rounded px-2 py-2 text-left transition-colors",
   601                    isActive
   602                      ? "bg-green-500/10 border border-green-500/30 hover:bg-green-500/20"
   603                      : "bg-bg-tertiary/50 border border-transparent hover:bg-bg-tertiary"
   604                  )}
   605                >
   606                  <div className="flex-shrink-0 mt-0.5">
   607                    {isActive ? (
   608                      <CheckCircle2 className="h-4 w-4 text-green-400" />
   609                    ) : (
   610                      <Circle className="h-4 w-4 text-text-tertiary" />
   611                    )}
   612                  </div>
   613                  <div className="flex-1 min-w-0">
   614                    <div className="flex items-center gap-2">
   615                      <span className={cn(
   616                        "text-xs font-medium",
   617                        isActive ? "text-green-400" : "text-text-primary"
   618                      )}>
   619                        {strategy.name}
   620                      </span>
   621                      <span className={cn(
   622                        "text-[9px] px-1 py-0.5 rounded",
   623                        strategy.mode === "all"
   624                          ? "bg-blue-500/20 text-blue-400"
   625                          : "bg-purple-500/20 text-purple-400"
   626                      )}>
   627                        {strategy.mode.toUpperCase()}
   628                      </span>
   629                    </div>
   630                    <div className="text-[10px] text-text-tertiary truncate">
   631                      {strategy.summary}
   632                    </div>
   633                  </div>
   634                </button>
   635              );
   636            })
   637          )}
   638        </div>
   639      )}
   640    </div>
   641  );
   642};
   643
   644// Stats Panel Component
   645const StatsPanel = ({ stats }: { stats: StatsData }) => {
   646  const isProfit = stats.totalPnlSol >= 0;
   647
   648  return (
   649    <div className="border-border-primary bg-bg-tertiary/30 grid grid-cols-4 gap-2 rounded-lg border p-3">
   650      <div className="text-center">
   651        <div className="text-text-primary text-lg font-bold">{stats.closedTrades}</div>
   652        <div className="text-text-secondary text-[10px]">Trades</div>
   653      </div>
   654      <div className="text-center">
   655        <div className={cn("text-lg font-bold", stats.winRate >= 0.5 ? "text-green-400" : "text-red-400")}>
   656          {(stats.winRate * 100).toFixed(0)}%
   657        </div>
   658        <div className="text-text-secondary text-[10px]">Win Rate</div>
   659      </div>
   660      <div className="text-center">
   661        <div className="text-text-primary text-lg font-bold">
   662          {stats.wins}W/{stats.losses}L
   663        </div>
   664        <div className="text-text-secondary text-[10px]">Record</div>
   665      </div>
   666      <div className="text-center">
   667        <div className={cn("text-lg font-bold", isProfit ? "text-green-400" : "text-red-400")}>
   668          {isProfit ? "+" : ""}
   669          <FormattedNumber value={stats.totalPnlSol} decimals={2} />
   670        </div>
   671        <div className="text-text-secondary text-[10px]">P&L (SOL)</div>
   672      </div>
   673    </div>
   674  );
   675};
   676
   677// Main Autopilot Widget Component
   678export const AutopilotWidget = () => {
   679  const { positions, signals, marketStatus, stats, scanTick, tokenList, config, strategies, isConnected, sendCommand } = useAutopilotWebSocket();
   680
   681  const totalTokens = tokenList.watchlist.length + tokenList.trending.length + tokenList.whales.length;
   682
   683  return (
   684    <div className="flex h-full flex-col gap-3 overflow-hidden">
   685      {/* Header - Market Status */}
   686      <div className="flex items-center justify-between">
   687        <div className="flex items-center gap-2">
   688          <Bot className={cn("h-5 w-5", isConnected ? "text-green-400" : "text-red-400")} />
   689          <span className="text-text-primary text-sm font-semibold">Token Autopilot</span>
   690          {scanTick && (
   691            <span className="text-text-secondary text-[10px]">
   692              #{scanTick.scanNumber} | {totalTokens} tokens
   693            </span>
   694          )}
   695        </div>
   696        <MarketStatusBadge status={marketStatus} />
   697      </div>
   698
   699      {/* Connection Status (shown when disconnected) */}
   700      {!isConnected && (
   701        <div className="bg-yellow-500/10 border-yellow-500/30 flex items-center gap-2 rounded-lg border px-3 py-2">
   702          <AlertTriangle className="h-4 w-4 text-yellow-400" />
   703          <span className="text-xs text-yellow-400">Connecting to autopilot backend...</span>
   704        </div>
   705      )}
   706
   707      {/* Controls Panel */}
   708      <ControlsPanel config={config} sendCommand={sendCommand} isConnected={isConnected} />
   709
   710      {/* Strategies Panel */}
   711      <StrategiesPanel strategies={strategies} sendCommand={sendCommand} isConnected={isConnected} />
   712
   713      {/* Stats Panel */}
   714      <StatsPanel stats={stats} />
   715
   716      {/* Open Positions */}
   717      {positions.length > 0 && (
   718        <div className="flex-shrink-0">
   719          <div className="text-text-secondary mb-2 flex items-center gap-2 text-xs font-medium">
   720            <Target className="h-3.5 w-3.5" />
   721            Open Positions ({positions.length})
   722          </div>
   723          <div className="max-h-40 space-y-2 overflow-auto">
   724            {positions.map((pos) => (
   725              <PositionCard key={pos.mint} position={pos} />
   726            ))}
   727          </div>
   728        </div>
   729      )}
   730
   731      {/* Watched Token Lists - Main scrollable area */}
   732      <div className="flex min-h-0 flex-1 flex-col gap-3 overflow-auto">
   733        {/* Watchlist */}
   734        <TokenListSection
   735          title="Watchlist"
   736          icon={Eye}
   737          tokens={tokenList.watchlist}
   738          iconColor="text-blue-400"
   739        />
   740
   741        {/* Trending */}
   742        <TokenListSection
   743          title="Trending"
   744          icon={Flame}
   745          tokens={tokenList.trending}
   746          iconColor="text-orange-400"
   747        />
   748
   749        {/* Whale Moves */}
   750        <TokenListSection
   751          title="Whale Moves"
   752          icon={Wallet}
   753          tokens={tokenList.whales}
   754          iconColor="text-green-400"
   755        />
   756
   757        {/* Empty state if no tokens */}
   758        {totalTokens === 0 && isConnected && (
   759          <div className="text-text-secondary border-border-primary/50 rounded-lg border border-dashed p-4 text-center text-xs">
   760            No tokens being watched. Enable auto-sources or add to watchlist.
   761          </div>
   762        )}
   763      </div>
   764
   765      {/* Signal Feed - Collapsible at bottom */}
   766      {signals.length > 0 && (
   767        <div className="flex-shrink-0 border-t border-border-primary/30 pt-2">
   768          <div className="text-text-secondary mb-1.5 flex items-center gap-2 text-xs font-medium">
   769            <Zap className="h-3.5 w-3.5" />
   770            Recent Signals
   771          </div>
   772          <div className="max-h-24 overflow-auto">
   773            <div className="space-y-0">
   774              {signals.slice(0, 5).map((signal, idx) => (
   775                <SignalItem key={`${signal.timestamp}-${idx}`} signal={signal} />
   776              ))}
   777            </div>
   778          </div>
   779        </div>
   780      )}
   781    </div>
   782  );
   783};
   784
   785export default AutopilotWidget;
   786

