import { useGlobalStore, useShallow } from "@/store/memoryStore/useGlobalStore";
import { useEffect } from "react";

export const RealtimeDataProvider = () => {
  const { startTimer, stopTimer } = useGlobalStore(
    useShallow(store => ({
      startTimer: store.timer.startTimer,
      stopTimer: store.timer.stopTimer,
    }))
  );

  useEffect(() => {
    startTimer();

    return () => stopTimer();
  }, [startTimer, stopTimer]);

  return null;
};

