import { useEffect, useId } from "react";
import { useGlobalStore, useShallow } from "@/store/memoryStore/useGlobalStore";
import { BaseWebsocket } from "@/websocket/base/BaseWebsocket";
import baseConnectionManager, {
  BaseConnectionCallbacks,
  defaultRefetchKey,
} from "@/websocket/base/BaseSubscriptionManager";

export const useBaseSubscription = <T extends BaseWebsocket>({
  connectionId,
  refetchKey = defaultRefetchKey,
  callbacks,
  websocket,
  isEnabled: isEnabledRaw = true,
}: {
  connectionId: string;
  refetchKey?: string;
  callbacks: BaseConnectionCallbacks;
  websocket?: T;
  isEnabled?: boolean;
}) => {
  const componentId = useId();
  const { isOffline } = useGlobalStore(
    useShallow(state => ({
      isOffline: state.connectionManager.isOffline,
    }))
  );

  const isEnabled = isEnabledRaw && !isOffline;

  useEffect(() => {
    baseConnectionManager.addSubscribedComponent(connectionId, componentId);
    baseConnectionManager.queueConnectionCheck({
      connectionId,
      callbacks,
      refetchKey,
      isEnabled,
    });

    return () => {
      baseConnectionManager.removeSubscribedComponent(
        connectionId,
        componentId
      );
      baseConnectionManager.queueConnectionCheck({
        connectionId,
        callbacks,
        refetchKey,
        isEnabled,
      });
    };
  }, [connectionId, refetchKey, isEnabled]);
  // Don't change deps here, componentId is constant by design and callbacks change shouldn't trigger additional check

  useEffect(() => {
    if (!websocket) return;

    const off = websocket.on("disconnected", () => {
      baseConnectionManager.queueReconnect(connectionId);
    });

    return off;
  }, [connectionId, refetchKey, isEnabled]);
  // Don't change deps here, componentId is constant by design and callbacks change shouldn't trigger additional check
};

