import Emittery from "emittery";
import { Healthcheck } from "@/websocket/healthcheck/Healthcheck";
import { GlobalStore } from "@/store/memoryStore/types";
import { StoreApi } from "zustand";
import { PersistentGlobalStore } from "@/store/persistentStore/types";
import { refreshAuthToken } from "@/api/auth/refreshAuthToken";
import { KEEP_ALIVE_INTERVAL } from "@/websocket/healthcheck/constants";
import { isDataTokenExpired, getDataAccessToken } from "@/utils/auth-tokens";

/**
 * The interval at which we send pings to the server to keep the connection
 * alive.
 */

// Map of event names to their event data
type EventMap = {
  connected: undefined;
  disconnected: CloseEvent;
};

// Pong message type for base websockets
type BaseWebsocketPong = { pong: null; timestamp: number };

/**
 * Base websocket class that provides connection management and healthcheck
 * functionality. This is similar to NatsWebsocket but without topic support.
 */
export abstract class BaseWebsocket {
  ws: WebSocket | undefined;
  store: StoreApi<GlobalStore>;
  persistentStore: StoreApi<PersistentGlobalStore>;
  healthcheck: Healthcheck<BaseWebsocketPong> | undefined;
  url: string;
  on: Emittery<EventMap>["on"];
  off: Emittery<EventMap>["off"];

  private keepAliveInterval: number;
  private emitter: Emittery<EventMap>;
  private readyPromise: Promise<void> | null = null;

  constructor({
    store,
    url,
    persistentStore,
    keepAliveInterval = KEEP_ALIVE_INTERVAL,
  }: {
    url: string;
    keepAliveInterval?: number;
    store: StoreApi<GlobalStore>;
    persistentStore: StoreApi<PersistentGlobalStore>;
  }) {
    this.url = url;
    this.store = store;
    this.persistentStore = persistentStore;
    this.keepAliveInterval = keepAliveInterval;
    this.emitter = new Emittery<EventMap>();
    this.on = this.emitter.on.bind(this.emitter);
    this.off = this.emitter.off.bind(this.emitter);
  }

  ready(): Promise<void> {
    // If already connected, return resolved promise
    if (this.ws?.readyState === WebSocket.OPEN) {
      return Promise.resolve();
    }

    // If we already have a pending ready promise, return it
    if (this.readyPromise) {
      return this.readyPromise;
    }

    // Create a new promise that resolves once the connection is open
    this.readyPromise = new Promise(resolve => {
      // Resolve once connected, then remove the listener
      const off = this.on("connected", () => {
        off();
        resolve();
      });
    });

    return this.readyPromise;
  }

  /**
   * Disconnect from the websocket server.
   */
  disconnect() {
    if (this.ws?.readyState === WebSocket.CLOSING) {
      return;
    }

    this.teardownConnectHandlers();

    this.readyPromise = null;

    // Close the websocket if it exists and isn't already closed
    if (this.ws && this.ws.readyState !== WebSocket.CLOSED) {
      this.ws.close();
    }

    // for proper cleanup
    this.ws = undefined;
  }

  protected setupConnectHandlers() {
    this.ws?.addEventListener("open", this.startHealthcheck);
    this.ws?.addEventListener("close", this.handleConnectionClosed);
  }

  private teardownConnectHandlers() {
    this.ws?.removeEventListener("open", this.startHealthcheck);
    this.ws?.removeEventListener("close", this.handleConnectionClosed);
  }

  private handleConnectionClosed = (e: CloseEvent) => {
    this.emitter.emit("disconnected", e);
  };

  private startHealthcheck = () => {
    if (!this.ws) return;

    this.emitter.emit("connected");

    const healthcheck = new BaseWebsocketHealthcheck({
      ws: this.ws,
      keepAliveInterval: this.keepAliveInterval,
    });

    this.healthcheck = healthcheck;

    this.healthcheck.on("healthcheck-failed", () => {
      this.store.getState().connectionManager.setConnectionUnstable(true);
    });

    this.healthcheck.on("stabilized", () => {
      this.store.getState().connectionManager.setConnectionUnstable(false);
      this.store.getState().connectionManager.setHideWarningByUser(false);
    });

    this.healthcheck.start();
  };

  protected async ensureValidDataToken(): Promise<string> {
    // If token is expired or expires soon, refresh it
    if (isDataTokenExpired()) {
      console.log(
        `[${new Date().toISOString()}] WS DEBUG: dataToken expired or expiring soon, refreshing before connect`
      );

      try {
        await refreshAuthToken();
        console.log(
          `[${new Date().toISOString()}] WS DEBUG: dataToken refreshed successfully before websocket connect`
        );
      } catch (error) {
        console.error(
          "Failed to refresh dataToken before websocket connect:",
          error
        );
        // Continue with existing token - the connection will fail and get handled elsewhere
      }
    }

    const token = getDataAccessToken();
    if (!token) {
      throw new Error("No data access token available");
    }

    return token;
  }

  abstract connect(...args: unknown[]): void;
}

/**
 * Healthcheck implementation for base websockets
 */
class BaseWebsocketHealthcheck extends Healthcheck<BaseWebsocketPong> {
  isPong(msg: unknown): msg is BaseWebsocketPong {
    if (
      typeof msg === "object" &&
      msg !== null &&
      "pong" in msg &&
      "timestamp" in msg
    ) {
      return true;
    }
    return false;
  }

  isPongHealthy(_pong: BaseWebsocketPong): boolean {
    // TODO: Implement once we have an `is_healthy` field
    return true;
  }

  getPingMessage() {
    return JSON.stringify({ action: "ping" });
  }
}

