import { MarketDataMessage } from "@/store/memoryStore/slices/websocket-messages-store/types";
import { GlobalStore } from "@/store/memoryStore/types";
import { Pubkey } from "@/types/basicTypes.model";
import { TopicName, WebsocketJsonDataMessage } from "@/websocket/topic/types";
import {
  TopicConstructorArgs,
  WebsocketTopic,
} from "@/websocket/topic/websocketTopic";
import { StoreApi } from "zustand";

/**
 * Type definition for market data topic names following the pattern:
 * market_data.{mint_address}
 *  Example: "market_data.7eMJmn1bYWSQEwxAX7CyngBzGNGu1cT582asKxxRpump"
 */
export type MarketDataTopicName = `market_data.${Pubkey}`;

/**
 * Module declaration to register the market_data topic in the global topic
 * registry. This enables type safety when working with market data topics
 * throughout the application.
 */
declare module "@/websocket/topic/types" {
  interface TopicRegistry {
    market_data: {
      name: MarketDataTopicName;
      args: {
        nats_replay: boolean;
      };
    };
  }
}

/**
 * WebSocket topic for real-time market data updates for specific tokens.
 *
 * This class manages:
 * - Real-time market data updates for a specific token mint
 * - Document visibility optimization (defers updates when tab is hidden)
 */
export class MarketDataTopic extends WebsocketTopic<MarketDataMessage> {
  private store: StoreApi<GlobalStore>;

  private mint: string;

  /**
   * Holds the most recent market data when document is hidden. Only the latest
   * message is kept to avoid memory buildup during long periods of inactivity.
   */
  private deferredMarketData: MarketDataMessage | null = null;

  /**
   * Interval ID for checking document visibility state. Runs every 2.5 seconds
   * when document is hidden to detect when it becomes visible again.
   */
  private visibilityInterval: ReturnType<typeof setInterval> | null = null;

  /**
   * Type guard to check if a topic name follows the market data pattern.
   */
  static isMarketDataTopic(topic: TopicName): topic is MarketDataTopicName {
    return topic.startsWith("market_data.");
  }

  constructor(
    args: TopicConstructorArgs<{
      store: StoreApi<GlobalStore>;
    }>
  ) {
    super(args);
    this.store = args.store;

    if (!MarketDataTopic.isMarketDataTopic(this.topic)) {
      throw new Error(
        `Cannot create MarketDataTopic with invalid topic: ${this.topic}`
      );
    }

    this.mint = this.parseTopic(this.topic);
  }

  /**
   * Handles incoming market data messages from the WebSocket.
   *
   * Implements document visibility optimization:
   * - If document is hidden: defers the update and starts visibility checking
   * - If document is visible: immediately updates the store
   *
   * This prevents unnecessary store updates and re-renders when the user
   * isn't actively viewing the application.
   */
  handleMessageData(data: WebsocketJsonDataMessage<MarketDataMessage>) {
    // Performance optimization: defer updates when tab is not visible unless
    // the user is on this mint's page
    if (this.shouldDeferMarketDataUpdate()) {
      this.deferredMarketData = data.message;
      if (!this.visibilityInterval) {
        this.startDocumentVisibilityInterval();
      }
      return;
    }

    // Document is visible - apply the update immediately
    this.store
      .getState()
      .websocketMessages.market_data.addItem(this.mint, data.message);
  }

  private shouldDeferMarketDataUpdate() {
    const isHiddenDocument =
      this.store.getState().documentVisibility.isHiddenDocument;

    const isOnMintPage = window.location.pathname.includes(this.mint);

    return isHiddenDocument && !isOnMintPage;
  }

  /**
   * Starts an interval that checks document visibility every 2.5 seconds.
   * When the document becomes visible again, applies any deferred market data
   * and cleans up the interval.
   */
  private startDocumentVisibilityInterval() {
    this.visibilityInterval = setInterval(() => {
      // If the document is still hidden, do nothing
      if (this.store.getState().documentVisibility.isHiddenDocument) {
        return;
      }
      // Otherwise if the document is visible, apply the deferred market data
      this.applyDeferredMarketData();
    }, 2500);
  }

  private applyDeferredMarketData() {
    if (!this.deferredMarketData) {
      return;
    }

    this.store
      .getState()
      .websocketMessages.market_data.addItem(
        this.mint,
        this.deferredMarketData
      );
    this.deferredMarketData = null;

    if (this.visibilityInterval) {
      clearInterval(this.visibilityInterval);
      this.visibilityInterval = null;
    }
  }

  private parseTopic(topic: MarketDataTopicName): string {
    const mint = topic.split(".")[1];
    return mint;
  }
}

