import { useBaseSubscription } from "./useBaseSubscription";
import { useMemo } from "react";
import { usePersistentGlobalStore } from "@/store/persistentStore/usePersistentGlobalStore.tsx";
import { connectActivePositionsWebsocket } from "@/websocket/active-positions/connectActivePositionsWebsocket.ts";
import { activePositionsWebsocket } from "@/websocket/active-positions/activePositionsWebsocketInstance.ts";
import { connectTickerTapeWebsocket } from "@/websocket/ticker-tape/connectTickerTapeWebsocket.ts";
import { tickerTapeWebsocket } from "@/websocket/ticker-tape/tickerTapeWebsocketInstance.ts";
import { useGetWatchlist } from "@/api/watchlist/useGetWatchlist.ts";
import { useGetWallets } from "@/api/user/useGetWallets.ts";
import { tokensToMintString } from "@/websocket/handlers/useWatchlistHandler/utils.ts";
import { useActiveWallet } from "@/hooks/useActiveWallet.ts";
import { useShallow } from "zustand/react/shallow";
import { useGlobalStore } from "@/store/memoryStore/useGlobalStore";
import { MIN_LIQUIDITY_SOL, MIN_POSITION_VALUE_USD } from "@/utils/constants";

export const useTickerTapeSubscription = () => {
  const { activeWallet, activeSubWalletsPubKeys, isBundle } = useActiveWallet();
  const { showTickerTape, tickerTapeTab, tickerTapePositionsSort } =
    usePersistentGlobalStore(
      useShallow(state => ({
        showTickerTape: state.userSettings.global.showTickerTape,
        tickerTapeTab: state.userSettings.global.tickerTapeTab,
        tickerTapePositionsSort:
          state.userSettings.global.tickerTapePositionsSort,
      }))
    );

  const orderParam = useMemo(
    () =>
      tickerTapePositionsSort === "value"
        ? "value_usd_desc"
        : "last_trade_desc",
    [tickerTapePositionsSort]
  );

  const address = isBundle
    ? activeSubWalletsPubKeys?.join(",")
    : activeWallet?.publicKey;

  const activePositionsItemsMap = useGlobalStore(
    state => state.websocketMessages.active_positions.itemsMap
  );

  const rawPnlData = useMemo(() => {
    if (tickerTapeTab !== "positions") return [];
    return Array.from(activePositionsItemsMap.values());
  }, [tickerTapeTab, activePositionsItemsMap]);

  const { data: watchlistTokens } = useGetWatchlist();
  const { data: walletsData } = useGetWallets({ isArchived: false });

  const userId = useMemo(() => {
    if (!walletsData) return undefined;

    const allWallets = [
      ...(walletsData.wallets ?? []),
      ...(walletsData.bundles?.flatMap(b => b.wallets ?? []) ?? []),
    ];

    const anyWalletWithUser = allWallets.find(w => w?.userId);
    return anyWalletWithUser?.userId as string | undefined;
  }, [walletsData]);

  const activePositionsParams = useMemo(() => {
    if (tickerTapeTab !== "positions" || !address) {
      return null;
    }
    return {
      address,
      sortBy: orderParam,
      minLiquiditySol: MIN_LIQUIDITY_SOL,
      minPositionValueUsd: MIN_POSITION_VALUE_USD,
      userId,
    };
  }, [tickerTapeTab, address, orderParam, userId]);

  useBaseSubscription({
    connectionId: "active-positions-subscription",
    refetchKey: activePositionsParams
      ? `${activePositionsParams.address}-${activePositionsParams.sortBy}-${activePositionsParams.userId}`
      : "none",
    isEnabled:
      !!activePositionsParams &&
      showTickerTape &&
      tickerTapeTab === "positions",
    callbacks: {
      onSubscribe: async () => {
        if (!activePositionsParams) {
          throw new Error("Active positions params are required");
        }
        return await connectActivePositionsWebsocket(activePositionsParams);
      },
    },
    websocket: activePositionsWebsocket,
  });

  const mintParam = useMemo(() => {
    if (!showTickerTape || tickerTapeTab !== "watchlist") return "";
    return tokensToMintString(watchlistTokens);
  }, [showTickerTape, tickerTapeTab, watchlistTokens]);

  useBaseSubscription({
    connectionId: "ticker-tape-watchlist-subscription",
    refetchKey: mintParam || "none",
    isEnabled:
      !!mintParam.trim() && showTickerTape && tickerTapeTab === "watchlist",
    callbacks: {
      onSubscribe: async () => {
        return await connectTickerTapeWebsocket(mintParam);
      },
    },
    websocket: tickerTapeWebsocket,
  });

  return {
    rawPnlData,
    watchlistTokens,
  };
};

