import Emittery from "emittery";
import { QueryClient } from "@tanstack/react-query";
import { Healthcheck } from "@/websocket/healthcheck/Healthcheck";
import { NatsWebsocketPong } from "@/types/websocket.model";
import { GlobalStore } from "@/store/memoryStore/types";
import { StoreApi } from "zustand";
import { TopicArgs, TopicName } from "@/websocket/topic/types";
import { createTopic, TopicInstance } from "@/websocket/nats/topicFactory";
import { PersistentGlobalStore } from "@/store/persistentStore/types";
import { NatsHealthcheck } from "@/websocket/nats/NatsHealthcheck";
import { refreshAuthToken } from "@/api/auth/refreshAuthToken";
