import { getDataAccessToken, isDataTokenExpired } from "@/utils/auth-tokens";

/**
 * The interval at which we send pings to the server to keep the connection
 * alive.
 */

// Map of event names to their event data
type EventMap = {
  connected: undefined;
  disconnected: CloseEvent;
};

export class NatsWebsocket {
  ws: WebSocket | undefined;
  queryClient: QueryClient;
  store: StoreApi<GlobalStore>;
  persistentStore: StoreApi<PersistentGlobalStore>;
  healthcheck: Healthcheck<NatsWebsocketPong> | undefined;
  url: string;
  on: Emittery<EventMap>["on"];
