   * Track active subscriptions by topic key, eg:
   * { "market_data.1234567890": TopicSubscription }
   */
  topics: Map<
    TopicName,
    {
      topic: TopicName;
      args?: Record<string, unknown>;
      topicInstance: TopicInstance;
    }
  > = new Map();

  private keepAliveInterval: number;
  private emitter: Emittery<EventMap>;
  private readyPromise: Promise<void> | null = null;
  private readyPromiseResolver: (() => void) | null = null;

  constructor({
    store,
    url,
    queryClient,
    persistentStore,
    keepAliveInterval = KEEP_ALIVE_INTERVAL,
  }: {
    url: string;
    keepAliveInterval?: number;
    queryClient: QueryClient;
    store: StoreApi<GlobalStore>;
    persistentStore: StoreApi<PersistentGlobalStore>;
  }) {
    this.url = url;
    this.queryClient = queryClient;
    this.store = store;
    this.persistentStore = persistentStore;
    this.keepAliveInterval = keepAliveInterval;
    this.emitter = new Emittery<EventMap>();
    this.on = this.emitter.on.bind(this.emitter);
    this.off = this.emitter.off.bind(this.emitter);
  }

  private getTopicInfo() {
    const user = this.queryClient.getQueryData<User>([queryKeys.userMe]);
    return {
      topic: user ? `swap.${user.telemetryId}` : undefined,
      topicV2: user ? `${WS_TOPIC_ENV}.swap.${user.telemetryId}` : undefined,
    };
