import { useQuery } from "@tanstack/react-query";
import { createQueryFn } from "@/api/api";
import { type DexScreenerResponse } from "@/api/dexscreener";
import {
  getDexScreenerBannerUrl,
  isDexScreenerPaid,
} from "@/api/utils/dexscreener";
import { queryKeys } from "@/api/queryKeys";
import { t } from "@lingui/core/macro";
import { useShallow } from "zustand/react/shallow";
import { useAuthStore } from "@/store/auth-store/auth-store";

interface UseDexScreenerDataResult {
  isPaid: boolean;
  bannerUrl: string | null;
  isLoading: boolean;
  error: Error | null;
  data: DexScreenerResponse | undefined;
}

export const useDexScreenerData = (
  tokenAddress: string | undefined,
  enabled: boolean
): UseDexScreenerDataResult => {
  const authStatus = useAuthStore(useShallow(state => state.authStatus));
  const { data, isLoading, error } = useQuery({
    queryKey: [queryKeys.dexscreener, tokenAddress],
    queryFn: createQueryFn<undefined, DexScreenerResponse>({
      method: "GET",
      path: `/tokens/${tokenAddress}`,
      client: "dexscreener",
    }),
    enabled: !!tokenAddress && enabled && authStatus === "authenticated",
    staleTime: 5 * 60 * 1000, // 5 minutes
    gcTime: 10 * 60 * 1000, // 10 minutes (was cacheTime in v4)
    retry: 1,
    meta: {
      errorMessage: t`Unable to fetch DEX Screener data.`,
    },
  });

  return {
    isPaid: data ? isDexScreenerPaid(data) : false,
    bannerUrl: data ? getDexScreenerBannerUrl(data) : null,
    isLoading,
    error,
    data,
  };
};

