import {
  QueryClient,
  QueryClientProvider,
  QueryCache,
  MutationCache,
} from "@tanstack/react-query";
import { toast } from "sonner";
import { Toast } from "@/components/toast";
import { isAxiosError } from "axios";
import { Button } from "@/components/button";
import React from "react";
import { OfflineToast } from "@/components/toasts/OfflineToast";
import { ConnectionRestoredToast } from "@/components/toasts/ConnectionRestoredToast";
import { t } from "@lingui/core/macro";
import { InvalidationManager } from "@/base/InvalidationManager";

const NO_INTERNET_NOTIFICATION_ID = "NO_INTERNET_CONNECTION";
const RESTORED_CONNECTION_NOTIFICATION_ID = "RESTORED_CONNECTION";

let isOffline = false;

// Shared error handling functions
const handleNetworkError = () => {
  toast.dismiss(RESTORED_CONNECTION_NOTIFICATION_ID);
  toast.custom(() => <OfflineToast />, {
    duration: Infinity,
    id: NO_INTERNET_NOTIFICATION_ID,
  });
  isOffline = true;
};

const handleConnectionRestored = () => {
  if (isOffline) {
    toast.dismiss(NO_INTERNET_NOTIFICATION_ID);
    toast.custom(() => <ConnectionRestoredToast />, {
      duration: 5000,
      id: RESTORED_CONNECTION_NOTIFICATION_ID,
    });
    isOffline = false;
  }
};

const shouldIgnoreUnauthorizedError = (error: unknown): boolean => {
  return isAxiosError(error) && error.response?.status === 401;
};

const handleGenericError = (
  errorMessage: string,
  error: unknown,
  { retry, buttonText }: { retry?: () => void; buttonText?: string }
) => {
  console.error(`Error: ${errorMessage}`, error);
  toast.custom(toastId => (
    <Toast
      onDismiss={() => toast.dismiss(toastId)}
      title={errorMessage}
      variant="error"
      rightContent={
        retry && (
          <Button
            variant="text"
            color="gray"
            borderless
            className="w-22 text-xs"
            onClick={() => {
              retry();
              toast.dismiss(toastId);
            }}
          >
            {buttonText ?? t`Try Again`}
          </Button>
        )
      }
    />
  ));
};

// Query cache with network error and offline logic
const queryCache = new QueryCache({
  onError: (error, query) => {
    if (isAxiosError(error) && error.code === "ERR_NETWORK") {
      handleNetworkError();
      return;
    }

    // Ignore 401 errors (unauthorized)
    if (shouldIgnoreUnauthorizedError(error)) {
      return;
    }

    if (query.meta?.errorMessage) {
      handleGenericError(query.meta.errorMessage as string, error, {
        retry: query.meta.retry,
        buttonText: query.meta.buttonText,
      });
    }
  },

  onSuccess: handleConnectionRestored,
});

// Mutation cache with network error handling
const mutationCache = new MutationCache({
  onError: (error, _variables, _context, mutation) => {
    if (isAxiosError(error) && error.code === "ERR_NETWORK") {
      handleNetworkError();
      return;
    }

    // Ignore 401 errors (unauthorized)
    if (shouldIgnoreUnauthorizedError(error)) {
      return;
    }

    if (mutation.meta?.errorMessage) {
      handleGenericError(mutation.meta.errorMessage as string, error, {
        retry: mutation.meta.retry,
        buttonText: mutation.meta.buttonText,
      });
    }
  },

  onSuccess: handleConnectionRestored,
});

// Create React Query client
export const queryClient = new QueryClient({
  queryCache,
  mutationCache,
  defaultOptions: {
    queries: {
      staleTime: 0,
      gcTime: 5 * 60 * 1000,
      retry: 0,
    },
    mutations: {
      retry: 0,
    },
  },
});

// Initialize InvalidationManager with the query client
const invalidationManager = InvalidationManager.getInstance();
invalidationManager.setQueryClient(queryClient);

export const ReactQueryProvider = ({ children }: React.PropsWithChildren) => {
  return (
    <QueryClientProvider client={queryClient}>{children}</QueryClientProvider>
  );
};

