     1→/**
     2→ * Tester Profile Store
     3→ *
     4→ * CRUD operations and queries for QA tester profiles stored in Vercel KV.
     5→ * Used for assignment scoring and workload management.
     6→ */
     7→
     8→import { kv } from './kv-client.js';
     9→import { KV_KEYS } from './kv-client.js';
    10→import type { TesterProfile } from '../types.js';
    11→
    12→/**
    13→ * Create or update a tester profile (upsert pattern).
    14→ * Used for syncing testers from Linear. Preserves historical stats if tester exists.
    15→ */
    16→export async function upsertTester(
    17→  data: Omit<TesterProfile, 'currentLoad' | 'totalCompleted' | 'avgCompletionTimeMs' | 'lastActiveAt'> & {
    18→    currentLoad?: number;
    19→    totalCompleted?: number;
    20→    avgCompletionTimeMs?: number;
    21→  }
    22→): Promise<TesterProfile> {
    23→  const existing = await getTester(data.id);
    24→
    25→  const tester: TesterProfile = {
    26→    // Preserve existing stats if not explicitly provided
    27→    currentLoad: data.currentLoad ?? existing?.currentLoad ?? 0,
    28→    totalCompleted: data.totalCompleted ?? existing?.totalCompleted ?? 0,
    29→    avgCompletionTimeMs: data.avgCompletionTimeMs ?? existing?.avgCompletionTimeMs ?? 0,
    30→    ...data,
    31→    lastActiveAt: new Date().toISOString(),
    32→  };
    33→
    34→  await kv.hset(KV_KEYS.tester(data.id), tester as unknown as Record<string, unknown>);
    35→  return tester;
    36→}
    37→
    38→/**
    39→ * Get a tester by ID.
    40→ * Returns null if tester doesn't exist.
    41→ */
    42→export async function getTester(id: string): Promise<TesterProfile | null> {
    43→  const data = await kv.hgetall(KV_KEYS.tester(id));
    44→  if (!data || Object.keys(data).length === 0) return null;
    45→  return data as unknown as TesterProfile;
    46→}
    47→
    48→/**
    49→ * Update a tester profile (partial update).
    50→ * Cannot change id or linearUserId after creation.
    51→ */
    52→export async function updateTester(
    53→  id: string,
    54→  updates: Partial<Omit<TesterProfile, 'id' | 'linearUserId'>>
    55→): Promise<TesterProfile | null> {
    56→  const existing = await getTester(id);
    57→  if (!existing) return null;
    58→
    59→  const updated: TesterProfile = {
    60→    ...existing,
    61→    ...updates,
    62→    lastActiveAt: new Date().toISOString(),
    63→  };
    64→
    65→  await kv.hset(KV_KEYS.tester(id), updated as unknown as Record<string, unknown>);
    66→  return updated;
    67→}
    68→
    69→/**
    70→ * List all testers.
    71→ * Uses scan pattern - may be slow with many testers. Use sparingly.
    72→ */
    73→export async function listTesters(): Promise<TesterProfile[]> {
    74→  const testers: TesterProfile[] = [];
    75→  for await (const key of kv.scanIterator({ match: 'testers:*' })) {
    76→    const data = await kv.hgetall(key);
    77→    if (data && Object.keys(data).length > 0) {
    78→      testers.push(data as unknown as TesterProfile);
    79→    }
    80→  }
    81→  return testers;
    82→}
    83→
    84→/**
    85→ * Get testers who are currently available for assignment.
    86→ */
    87→export async function getAvailableTesters(): Promise<TesterProfile[]> {
    88→  const all = await listTesters();
    89→  return all.filter(t => t.availability === 'available');
    90→}
    91→
    92→/**
    93→ * Increment tester's current workload count.
    94→ * Call when assigning a ticket to a tester.
    95→ */
    96→export async function incrementTesterLoad(id: string): Promise<void> {
    97→  const tester = await getTester(id);
    98→  if (tester) {
    99→    await updateTester(id, { currentLoad: tester.currentLoad + 1 });
   100→  }
   101→}
   102→
   103→/**
   104→ * Decrement tester's current workload count.
   105→ * Call when a ticket is completed or unassigned.
   106→ */
   107→export async function decrementTesterLoad(id: string): Promise<void> {
   108→  const tester = await getTester(id);
   109→  if (tester && tester.currentLoad > 0) {
   110→    await updateTester(id, { currentLoad: tester.currentLoad - 1 });
   111→  }
   112→}
   113→

