        if (latestPosition.lastTradeTimestamp > prev.timestamp) {
          newFlashes.set(token.mint, latestPosition.isBuying ? "buy" : "sell");
        }
      }

      // Update ref
      if (latestPosition) {
        prevActivityRef.current.set(token.mint, {
