function formatMc(mc: number): string {
  if (mc >= 1_000_000) return `${(mc / 1_000_000).toFixed(1)}M`;
  if (mc >= 1_000) return `${(mc / 1_000).toFixed(0)}K`;
  return mc.toFixed(0);
}

