import { spawn, type Subprocess } from "bun";
import { resolve } from "path";

const processes = new Set<Subprocess>();

const cleanup = () => {
  for (const proc of processes) {
    try {
      proc.kill();
    } catch {
      // Ignore errors if process already dead
    }
  }
};

process.on("SIGINT", () => {
  cleanup();
  process.exit(1);
});

process.on("SIGTERM", () => {
  cleanup();
  process.exit(1);
});

async function run(args: string[], name: string, cwd?: string) {
  console.log(`▶️  [${name}] Starting...`);
  const start = performance.now();

  const proc = spawn(args, {
    stdio: ["ignore", "inherit", "inherit"],
    env: { ...process.env, FORCE_COLOR: "1" },
    cwd,
  });

  processes.add(proc);

  const exitCode = await proc.exited;
  processes.delete(proc);

  const duration = ((performance.now() - start) / 1000).toFixed(2);

  if (exitCode !== 0) {
    console.error(`❌ [${name}] Failed in ${duration}s`);
    cleanup();
    process.exit(exitCode);
  }

  console.log(`✅ [${name}] Completed in ${duration}s`);
}

// Get paths
const repoRoot = resolve(import.meta.dir, "../..");
const packageDir = resolve(repoRoot, "apps/web-terminal");

console.log("🚀 Starting build process...");
const totalStart = performance.now();

try {
  // Change to package directory for build operations
  process.chdir(packageDir);

  try {
    const { statSync } = await import("node:fs");
    const stats = statSync(
      "./node_modules/.cache/tsbuildinfo/tsconfig.app.tsbuildinfo"
    );
    console.log(`ℹ️  TS Build Info size: ${(stats.size / 1024).toFixed(2)}KB`);
  } catch {
    console.log("⚠️  TS Build Info file not found");
  }

  // Prepare dependencies (i18n only - openapi is committed to git)
  await run(["bun", "--bun", "run", "translate:pull"], "i18n", packageDir);

  // Run typecheck and build in parallel
  await Promise.all([
    run(["bun", "--bun", "run", "typecheck:noemit"], "typecheck", packageDir),
    run(["bun", "--bun", "run", "vite", "build"], "vite", packageDir),
  ]);

  const totalDuration = ((performance.now() - totalStart) / 1000).toFixed(2);
  console.log(`🎉 Build completed successfully in ${totalDuration}s!`);
} catch (error) {
  console.error("Build failed unexpectedly:", error);
  cleanup();
  process.exit(1);
}

