// Create user bubble at center (only if doesn't exist)
const canvas = canvasRef.current;
const width = canvas?.width || 800;
const height = canvas?.height || 600;
const startX = width / 2;
const startY = height / 2;

// Use lowercase for consistency with how KOL bubbles and Intel Grid store IDs
const userWalletKeyLower = userWalletKey.toLowerCase();

const userBubble: TraderBubble = {
  id: userWalletKeyLower,
  name: "You",
  icon: null,
  emoji: "👤",
  source: "user",
  isUser: true,
  buys: 0,
  sells: 0,
  buyVolume: 0,
  sellVolume: 0,
  netPosition: 0,
  volume: 0,
  pnl: 0,
  momentumSum: 0,
  momentumVolume: 0,
  avgMomentum: 0,
  x: startX,
  y: startY,

