          return;
        }
        const img = new Image();
        img.crossOrigin = "anonymous";
        img.onload = () => {
          imagesRef.current.set(url, img);
          resolve(img);
        };
        img.onerror = () => {
          imagesRef.current.set(url, null);
          resolve(null);
        };
        img.src = url;
