/* eslint-disable lingui/no-unlocalized-strings */
import { i18n, Locale, Messages } from "@lingui/core";
import EnIcon from "@/assets/icons/locales/en.svg";
import RuIcon from "@/assets/icons/locales/ru.svg";
// import EsIcon from "@/assets/icons/locales/es.svg";
import ZhIcon from "@/assets/icons/locales/zh.svg";
import KoIcon from "@/assets/icons/locales/ko.svg";

export const locales = {
  en: "English",
  zh: "中文",
  ko: "한국어",
  ru: "Русский",
  // es: "Español",
} as const;

export const localeIcons = {
  en: EnIcon,
  zh: ZhIcon,
  ko: KoIcon,
  ru: RuIcon,
  // es: EsIcon,
};

export const availableLocales = Object.keys(locales) as Locale[];
export const defaultLocale = "en";

// Preload default messages so fallback does not rely on a dynamic import
import { messages as defaultMessages } from "@/locales/en.ts";

// Use Vite's module graph to locate locale files at build time
// Eager=true so all locales are bundled and no runtime network requests are needed
const localeModules = import.meta.glob("@/locales/*.ts", {
  import: "messages",
  eager: true,
}) as Record<string, Messages>;

const getLocaleMessages = (locale: Locale): Messages | null => {
  const normalized = `${locale}.ts`;
  const entry = Object.entries(localeModules).find(([path]) =>
    path.endsWith(`/${normalized}`)
  );
  return entry?.[1] ?? null;
};

async function loadLocaleMessages(locale: Locale): Promise<Messages | null> {
  try {
    return getLocaleMessages(locale);
  } catch (err) {
    console.warn(`Failed to load locale "${locale}":`, err);

