export type DiscoveryStore = {
  isTrendingDataLoading: boolean;
  isFilterSheetOpen: boolean;
  /**
   * The ID of the preset being edited in the Discovery filter sheet.
   * - undefined: No preset is being edited
   * - "new": A new preset is being created (use draftPresetName for the name)
   * - string ID: An existing preset is being edited (fetch by ID)
   */
  editingPresetId: string | undefined;
  draftPresetName: string | undefined;

  setIsTrendingDataLoading: (isLoading: boolean) => void;
  setIsFilterSheetOpen: (isOpen: boolean) => void;
  setEditingPresetId: (id: string | undefined) => void;
  setDraftPresetName: (name: string | undefined) => void;
};

