export type WatchlistTokenDeleteActionType = "deleting" | "processed";

type PendingRemovalTokensAction =
  | { type: "clear" }
  | { type: "add" | "remove"; watchedTokenMint: string };

export type WatchlistStore = {
  isWatchlistDataLoading: boolean;
  pendingRemovalTokens: Set<string>;
  deletingWatchedTokenSet: Set<string>;

  setIsWatchlistDataLoading: (value: boolean) => void;
  updateDeletingWatchedTokenSet: (
    watchedTokenMint: string,
    action: WatchlistTokenDeleteActionType
  ) => void;
  updatePendingRemovalTokensSet: (action: PendingRemovalTokensAction) => void;
};

