/**
 * Autopilot Message Parsing Tests
 *
 * Tests for WebSocket message parsing logic extracted from connectAutopilotWebsocket.
 * Validates all event types, unknown events, malformed data, and pong message filtering.
 */

import { describe, expect, it } from "vitest";
import type {
  AutopilotSignal,
  AutopilotMarketStatus,
  AutopilotStats,
  AutopilotScanTick,
  AutopilotTokenList,
  AutopilotConfig,
  AutopilotStrategies,
} from "@/store/memoryStore/slices/autopilot-store/types";

// Backend event types (matching connectAutopilotWebsocket.ts)
interface BackendPositionData {
  mint: string;
  symbol: string;
  action: "buy" | "sell";
  entryPriceSol: number;
  positionSizeSol: number;
  entryTime: number;
}

type AutopilotEvent =
  | { type: "position_update"; positions: BackendPositionData[] }
  | { type: "signal"; signal: AutopilotSignal }
  | { type: "market_status"; status: AutopilotMarketStatus }
  | { type: "stats_update"; stats: AutopilotStats }
  | { type: "scan_tick"; tick: AutopilotScanTick }
  | { type: "token_list"; tokens: AutopilotTokenList }
  | { type: "config"; config: AutopilotConfig }
  | { type: "strategies"; strategies: AutopilotStrategies };

/**
 * Pure function to parse autopilot WebSocket events.
 * Returns null for pong messages, malformed data, or unknown event types.
 */
export function parseAutopilotEvent(data: unknown): AutopilotEvent | null {
  // Skip pong messages
  if (data && typeof data === "object" && "pong" in data) {
    return null;
  }

  // Validate data is an object with a type field
  if (!data || typeof data !== "object" || !("type" in data)) {
    return null;
  }

  const event = data as AutopilotEvent;

  // Validate known event types
  const validTypes = [
    "position_update",
    "signal",
    "market_status",
    "stats_update",
    "scan_tick",
    "token_list",
    "config",
    "strategies",
  ];

  if (!validTypes.includes(event.type)) {
    return null;
  }

  return event;
}

describe("parseAutopilotEvent", () => {
  describe("valid event types", () => {
    it("parses position_update event correctly", () => {
      const data = {
        type: "position_update",
        positions: [
          {
            mint: "mint1",
            symbol: "TOKEN1",
            action: "buy" as const,
            entryPriceSol: 0.001,
            positionSizeSol: 1.0,
            entryTime: Date.now(),
          },
        ],
      };

      const result = parseAutopilotEvent(data);

      expect(result).not.toBeNull();
      expect(result?.type).toBe("position_update");
      if (result?.type === "position_update") {
        expect(result.positions.length).toBe(1);
        expect(result.positions[0].symbol).toBe("TOKEN1");
      }
    });

