     1/**
     2 * Volume Spike Rule
     3 *
     4 * Triggers when volume in a timeframe exceeds a multiplier of the average.
     5 * Uses buy/sell volume to determine direction.
     6 */
     7
     8import type { Rule, VolumeSpkeRuleConfig, Signal, AnalysisContext } from './types.js';
     9
    10export class VolumeSpikeRule implements Rule<VolumeSpkeRuleConfig> {
    11  constructor(public config: VolumeSpkeRuleConfig) {}
    12
    13  analyze(context: AnalysisContext): Signal | null {
    14    if (!this.config.enabled) return null;
    15
    16    const { token } = context;
    17    const { timeframe, multiplier } = this.config;
    18
    19    // Get volume for current timeframe
    20    const buyVolumeKey = `buy_volume_sol_${timeframe}` as keyof typeof token;
    21    const sellVolumeKey = `sell_volume_sol_${timeframe}` as keyof typeof token;
    22
    23    const buyVolume = (token[buyVolumeKey] as number) ?? 0;
    24    const sellVolume = (token[sellVolumeKey] as number) ?? 0;
    25    const currentVolume = buyVolume + sellVolume;
    26
    27    if (currentVolume === 0) {
    28      return null; // No volume data
    29    }
    30
    31    // Compare with longer timeframe to get baseline
    32    // For 5m, compare with 1h. For 1h, compare with 6h. etc.
    33    const baselineTimeframe = this.getBaselineTimeframe(timeframe);
    34    const baselineBuyKey = `buy_volume_sol_${baselineTimeframe}` as keyof typeof token;
    35    const baselineSellKey = `sell_volume_sol_${baselineTimeframe}` as keyof typeof token;
    36    const baselineBuy = (token[baselineBuyKey] as number) ?? 0;
    37    const baselineSell = (token[baselineSellKey] as number) ?? 0;
    38    const baselineVolume = baselineBuy + baselineSell;
    39
    40    if (baselineVolume === 0) {
    41      return null; // Can't calculate baseline
    42    }
    43
    44    // Calculate normalized volume (per-timeframe average)
    45    const timeframeMinutes = this.getTimeframeMinutes(timeframe);
    46    const baselineMinutes = this.getTimeframeMinutes(baselineTimeframe);
    47    const baselineAvgPerTimeframe = (baselineVolume / baselineMinutes) * timeframeMinutes;
    48
    49    // Check if current volume exceeds threshold
    50    const volumeRatio = currentVolume / baselineAvgPerTimeframe;
    51
    52    if (volumeRatio < multiplier) {
    53      return null; // Below threshold
    54    }
    55
    56    // Determine if it's buy or sell dominated
    57    const buyDominant = buyVolume > sellVolume;
    58    const dominanceRatio = buyDominant
    59      ? buyVolume / (sellVolume || 1)
    60      : sellVolume / (buyVolume || 1);
    61
    62    // Signal strength based on how much it exceeds threshold
    63    const strength = Math.min(volumeRatio / (multiplier * 2), 1);
    64
    65    // Determine signal type based on buy/sell dominance
    66    let signalType: 'buy' | 'sell' | 'alert' = 'alert';
    67    if (this.config.type === 'buy' && buyDominant) signalType = 'buy';
    68    if (this.config.type === 'sell' && !buyDominant) signalType = 'sell';
    69    if (this.config.type === 'both') {
    70      signalType = buyDominant ? 'buy' : 'sell';
    71    }
    72
    73    return {
    74      ruleId: this.config.id,
    75      ruleName: this.config.name,
    76      mint: token.mint,
    77      symbol: token.symbol,
    78      type: signalType,
    79      strength,
    80      reason: `Volume spike ${volumeRatio.toFixed(1)}x average in ${timeframe} (${buyDominant ? 'buy' : 'sell'} dominated ${dominanceRatio.toFixed(1)}:1)`,
    81      data: {
    82        currentVolume,
    83        baselineAvgPerTimeframe,
    84        volumeRatio,
    85        buyVolume,
    86        sellVolume,
    87        buyDominant,
    88        dominanceRatio,
    89        timeframe,
    90        multiplier,
    91      },
    92      timestamp: Date.now(),
    93    };
    94  }
    95
    96  private getBaselineTimeframe(timeframe: string): string {
    97    switch (timeframe) {
    98      case '5m': return '1h';
    99      case '1h': return '6h';
   100      case '6h': return '24h';
   101      case '24h': return '24h'; // Use same (can't go higher)
   102      default: return '24h';
   103    }
   104  }
   105
   106  private getTimeframeMinutes(timeframe: string): number {
   107    switch (timeframe) {
   108      case '5m': return 5;
   109      case '1h': return 60;
   110      case '6h': return 360;
   111      case '24h': return 1440;
   112      default: return 60;
   113    }
   114  }
   115}
   116
   117// Factory function for easy creation
   118export function createVolumeSpikeRule(
   119  id: string,
   120  name: string,
   121  options: {
   122    timeframe: VolumeSpkeRuleConfig['timeframe'];
   123    multiplier: number;
   124    type?: 'buy' | 'sell' | 'both';
   125  }
   126): VolumeSpikeRule {
   127  return new VolumeSpikeRule({
   128    id,
   129    name,
   130    description: `Triggers when volume is ${options.multiplier}x average in ${options.timeframe}`,
   131    enabled: true,
   132    type: options.type ?? 'both',
   133    ruleType: 'volume_spike',
   134    timeframe: options.timeframe,
   135    multiplier: options.multiplier,
   136  });
   137}
