import { useWatchlistSubscription } from "@/hooks/useSubscriptions/useWatchlistSubscription";
import { WatchlistTableView } from "./watchlist-widget-table-view";

export const WatchlistWidget = () => {
  useWatchlistSubscription();

  return (
    <div className="@container flex h-full flex-col">
      <div className="flex-1 overflow-hidden">
        <WatchlistTableView selectedTimeFrame="5m" />
      </div>
    </div>
  );
};

