import { createColumnHelper, type ColumnDef } from "@tanstack/react-table";
import { useMemo } from "react";
import { Table } from "@/components/table/table";
import { TABLE_ROW_HEIGHT_SMALL } from "@/config";
import { useShallow } from "zustand/react/shallow";
import { cn } from "@/lib/utils";
import { useGlobalStore } from "@/store/memoryStore/useGlobalStore";
import { useGetWatchlist } from "@/api/watchlist/useGetWatchlist";
import { WatchlistTokenData } from "@/store/memoryStore/slices/websocket-messages-store/types";
import { SymbolCell } from "@/ui/discovery/lists/shared/cells/symbol-cell";
import { McapCell } from "@/ui/discovery/lists/shared/cells/mcap-cell";
import { PriceChangeCell } from "@/ui/discovery/lists/shared/cells/price-change-cell";
import { TotalVolumeCell } from "@/ui/discovery/lists/shared/cells/total-volume-cell";
import { LiquidityCell } from "@/ui/discovery/lists/shared/cells/liquidity-cell";
import { calculatePriceChange } from "@/ui/discovery/lists/shared/utils";
import { Star } from "lucide-react";
import { usePausedData } from "@/hooks/usePausedData";
import { t } from "@lingui/core/macro";
import { PERCENT_SIGN } from "@/utils/constants";

const columnHelper = createColumnHelper<WatchlistTokenData>();

export type TimeFrame = "5m" | "1h" | "6h";

type WatchlistTableViewProps = {
  selectedTimeFrame: TimeFrame;
};

const priceChangeAccessors = {
  "5m": (row: WatchlistTokenData) =>
    calculatePriceChange(
      Number(row.market_data.price_usd),
      Number(row.market_data.trading_stats_5m.earliest_price_usd)
    ),
  "1h": (row: WatchlistTokenData) =>
    calculatePriceChange(
      Number(row.market_data.price_usd),
      Number(row.market_data.trading_stats_1h.earliest_price_usd)
    ),
  "6h": (row: WatchlistTokenData) =>
    calculatePriceChange(
      Number(row.market_data.price_usd),
      Number(row.market_data.trading_stats_6h.earliest_price_usd)
    ),
};

export const WatchlistTableView = ({
  selectedTimeFrame,
}: WatchlistTableViewProps) => {
  const { data: watchlistPositions } = useGetWatchlist();

  const { watchlistMap, openLocation, isWatchlistLoading } = useGlobalStore(
    useShallow(state => ({
      watchlistMap: state.websocketMessages.watchlist.itemsMap,
      isWatchlistLoading: state.watchlist.isWatchlistDataLoading,
      openLocation: state.chartPreview.openLocation,
    }))
  );

  const positionMap = useMemo(() => {
    const positionMap = new Map<string, boolean>();

    if (watchlistPositions?.watchlistTokens?.length) {
      for (const group of watchlistPositions.watchlistTokens) {
        if (!group?.watchlistTokens?.length) continue;

        const tokens = group.watchlistTokens;

        for (const token of tokens) {
          positionMap.set(token.tokenMint, true);
        }
      }
    }

    return positionMap;
  }, [watchlistPositions]);

  const liveData = useMemo(() => {
    const arr = Array.from(watchlistMap.values()).filter(token =>
      positionMap.has(token.mint)
    );

    // Sort by market cap descending
    return arr.sort((a, b) => {
      const mcapA = Number(a.market_data.market_cap_usd || 0);
      const mcapB = Number(b.market_data.market_cap_usd || 0);
      return mcapB - mcapA;
    });
  }, [watchlistMap, positionMap]);

  const data = usePausedData({
    liveData,
    isPaused: openLocation === "watchlist",
  });

  const staticColumns = useMemo(
    () => [
      columnHelper.accessor(row => row.market_data.symbol, {
        id: "symbol",
        header: t`Token`,
        cell: props => (
          <SymbolCell
            {...props}
            showWatchlistToggle
            chartPreviewLocation="watchlist"
          />
        ),
        size: 140,
        minSize: 120,
        enableSorting: true,
      }),
      columnHelper.accessor(row => Number(row.market_data.market_cap_usd), {
        id: "mcap",
        header: t`MCap`,
        cell: McapCell,
        size: 100,
        enableSorting: true,
      }),
    ],
    []
  );

  const volumeAndLiquidityColumns = useMemo(
    () => [
      columnHelper.accessor(row => Number(row.market_data.volume_24h_usd), {
        id: "volume",
        header: t`Volume`,
        cell: TotalVolumeCell,
        size: 100,
        enableSorting: true,
      }),
      columnHelper.accessor(row => Number(row.market_data.liquidity_usd), {
        id: "liquidity_usd",
        header: t`Liquidity`,
        cell: LiquidityCell,
        size: 100,
        enableSorting: true,
      }),
    ],
    []
  );

  const priceChangeColumns = useMemo(() => {
    const createColumn = (tf: TimeFrame, header: string) =>
      columnHelper.accessor(priceChangeAccessors[tf], {
        id: `price_change_pct_${tf}`,
        header: () => header,
        cell: PriceChangeCell,
        size: 80,
        enableSorting: true,
      }) as ColumnDef<WatchlistTokenData>;

    return {
      "5m": createColumn("5m", t`5m ${PERCENT_SIGN}`),
      "1h": createColumn("1h", t`1h ${PERCENT_SIGN}`),
      "6h": createColumn("6h", t`6h ${PERCENT_SIGN}`),
    };
  }, []);

  const columns = useMemo(() => {
    return [
      ...staticColumns,
      priceChangeColumns[selectedTimeFrame],
      ...volumeAndLiquidityColumns,
    ];
  }, [
    selectedTimeFrame,
    staticColumns,
    priceChangeColumns,
    volumeAndLiquidityColumns,
  ]);

  const isTableLoading =
    isWatchlistLoading &&
    !(
      (watchlistPositions?.watchlistTokens?.[0]?.watchlistTokens?.length ??
        0) === 0
    );

  return (
    <div className="bg-bg-primary-hover h-full overflow-hidden rounded">
      <Table
        columns={columns}
        data={data}
        isLoading={isTableLoading}
        estimateRowSize={TABLE_ROW_HEIGHT_SMALL + 8}
        rowHeight="small"
        className="hide-scrollbar h-full"
        getRowId={row => row.mint}
        tableRowClassName="bg-bg-tertiary hover:bg-bg-quartiary cursor-pointer transition-colors rounded"
        tableCellClassName={cn(
          "bg-inherit first:rounded-l last:rounded-r",
          selectedTimeFrame === "5m"
            ? "5m"
            : selectedTimeFrame === "1h"
              ? "1h"
              : "6h"
        )}
        tableHeadRowClassName="whitespace-nowrap"
        getRowUrl={row => `/trading/${row.original.mint}`}
        rowGap={8}
        storeKey="watchlist"
        noDataIcon={<Star />}
        noDataDescription={t`No tokens in watchlist`}
        hideHeaderOnNoData
      />
    </div>
  );
};

