import { useBaseSubscription } from "./useBaseSubscription";
import { useMemo } from "react";
import { useGlobalStore, useShallow } from "@/store/memoryStore/useGlobalStore";
import { getDataHistory } from "@/api/data/useDataHistory";
import { tokensToMintString } from "@/websocket/handlers/useWatchlistHandler/utils";
import { useGetWatchlist } from "@/api/watchlist/useGetWatchlist";
import { connectWatchlistWebsocket } from "@/websocket/watchlist/connectWatchlistWebsocket";
import { watchlistWebsocket } from "@/websocket/watchlist/WatchlistWebsocketInstance";
import { WatchlistTokenData } from "@/store/memoryStore/slices/websocket-messages-store/types";

export const useWatchlistSubscription = () => {
  const { data: trackedTokens } = useGetWatchlist();

  const {
    setInitialData,
    setIsInitialWatchlistDataLoading,
    updatePendingRemovalTokensSet,
    deletingWatchedTokenSet,
  } = useGlobalStore(
    useShallow(state => ({
      deletingWatchedTokenSet: state.watchlist.deletingWatchedTokenSet,
      setInitialData: state.websocketMessages.watchlist.setData,
      setIsInitialWatchlistDataLoading:
        state.watchlist.setIsWatchlistDataLoading,
      updatePendingRemovalTokensSet:
        state.watchlist.updatePendingRemovalTokensSet,
    }))
  );

  const wsParams = useMemo(() => {
    if (deletingWatchedTokenSet.size === 0) {
      return tokensToMintString(trackedTokens);
    }

    return "";
  }, [trackedTokens, deletingWatchedTokenSet]);

  useBaseSubscription({
    connectionId: "watchlist-subscription",
    refetchKey: wsParams || "none",
    isEnabled: !!wsParams,
    callbacks: {
      onInitialData: async () => {
        setIsInitialWatchlistDataLoading(true);

        const data = await getDataHistory<WatchlistTokenData[]>(
          `watchlist?mint=${wsParams}`
        );

        if (deletingWatchedTokenSet.size === 0) {
          updatePendingRemovalTokensSet({ type: "clear" });
          setInitialData({
            items: data,
            resetPreviousData: true,
          });
        }

        setIsInitialWatchlistDataLoading(false);
      },
      onSubscribe: async () => {
        return await connectWatchlistWebsocket();
      },
    },
    websocket: watchlistWebsocket,
  });
};

