import React from "react";
import type { CellContext } from "@tanstack/react-table";
import { FormattedNumber } from "@/components/formatted-number/formatted-number";
import type { WatchlistTokenData } from "@/store/memoryStore/slices/websocket-messages-store/types";

type LiquidityCellComponent = <R extends WatchlistTokenData>(
  ctx: CellContext<R, number>
) => React.ReactElement;

const LiquidityCellComponent: LiquidityCellComponent = ({ getValue }) => (
  <FormattedNumber
    preset="usd"
    value={getValue()}
  />
);

export const LiquidityCell = React.memo(
  LiquidityCellComponent,
  (prev, next) => prev.getValue() === next.getValue()
) as LiquidityCellComponent;

