import React from "react";
import type { CellContext } from "@tanstack/react-table";
import { TokenImage } from "@/components/token-image";
import { getBonkbotImageUrl } from "@/utils/asset-mappers";
import { ChartPreviewTooltip } from "@/components/TradingViewChart";
import { WatchlistToggle } from "@/components/watchlist-toggle/watchlist-toggle.tsx";
import { WatchlistTokenData } from "@/store/memoryStore/slices/websocket-messages-store/types";
import { useLiveStreamBadge } from "@/components/live-stream-badge";
import type { ChartPreviewLocation } from "@/store/persistentStore/slices/chart-preview-store/types";
import { useGlobalStore } from "@/store/memoryStore/useGlobalStore";
import { cn } from "@/lib/utils";

type SymbolCellComponentProps<R extends WatchlistTokenData> = CellContext<
  R,
  string
> & {
  showWatchlistToggle?: boolean;
  chartPreviewLocation: ChartPreviewLocation;
};

type SymbolCellComponent = <R extends WatchlistTokenData>(
  props: SymbolCellComponentProps<R>
) => React.ReactElement;

const SymbolCellComponent: SymbolCellComponent = ({
  getValue,
  cell,
  showWatchlistToggle,
  chartPreviewLocation,
}) => {
  const { imgSrc, isCrossOrigin } = getBonkbotImageUrl(cell.row.original.mint);

  const {
    mint,
    market_data: { symbol },
  } = cell.row.original;

  const chartPreviewOpenLocation = useGlobalStore(
    state => state.chartPreview.openLocation
  );

  const isChartPreviewOpen = chartPreviewOpenLocation === chartPreviewLocation;

  const { component: liveStreamBadge, isLive } = useLiveStreamBadge({
    mint,
    symbol,
    className: `px-1 py-0.5 text-[10px]`,
  });

  return (
    <div className="flex items-center gap-3">
      {showWatchlistToggle && (
        <WatchlistToggle
          mint={mint}
          ticker={symbol}
        />
      )}

      <div className="flex items-center justify-center gap-1.5 overflow-hidden">
        <ChartPreviewTooltip
          mint={mint}
          location={chartPreviewLocation}
          tokenSymbol={cell.row.original.market_data.symbol}
          tokenName={cell.row.original.market_data.name}
          side="right"
          align="start"
          sideOffset={isLive ? 40 : 8}
        >
          <div className="flex items-center gap-1.5">
            <TokenImage
              symbol={symbol}
              imgSrc={imgSrc}
              isCrossOrigin={isCrossOrigin}
              alt=""
              className="size-3.5 min-w-3.5 rounded"
            />
            <p className="text-text-primary truncate text-sm font-medium">
              {getValue() || cell.row.original.market_data?.name}
            </p>
          </div>
        </ChartPreviewTooltip>
        <div className={cn(isChartPreviewOpen && "pointer-events-none")}>
          {liveStreamBadge}
        </div>
      </div>
    </div>
  );
};

export const SymbolCell = React.memo(
  SymbolCellComponent,
  (prevProps, nextProps) =>
    prevProps.getValue() === nextProps.getValue() &&
    prevProps.cell.row.original.market_data.image ===
      nextProps.cell.row.original.market_data.image &&
    prevProps.showWatchlistToggle === nextProps.showWatchlistToggle &&
    prevProps.chartPreviewLocation === nextProps.chartPreviewLocation
) as SymbolCellComponent;

