import type { LimitOrderType } from "@/api/orders/types.ts";
import type { PnlData, PnlQueryResult } from "@/api/user/types.ts";
import type {
  DecimalString,
  IntegerString,
  PercentageString,
  PercentString,
  Pubkey,
  TimeResolution,
  UnixNanoTimestamp,
} from "@/types/basicTypes.model";
import type {
  LiveTrade,
  SummaryRecord,
  TokenSummary,
} from "../wallet-tracking-store/types";

////////////////////////////////////////////////////////////
// Slice related configuration
////////////////////////////////////////////////////////////

export type MessagesSlice<T> = {
  itemsMap: Map<string, T>;
  isInitialDataLoading: boolean;
  addItem: (
    item: T,
    options?: { skipSortOnItemUpdate?: boolean; mergeItem?: boolean }
  ) => void;
  removeItem: (id: string) => void;
  reset: () => void;
  setData: ({
    items,
    timeField,
    resetPreviousData,
  }: {
    items: T[];
    timeField?: keyof T;
    resetPreviousData?: boolean;
  }) => void;
  setIsInitialDataLoading: (isInitialDataLoading: boolean) => void;
};

export type KeyedMessagesSlice<T> = {
  data: { [key in Pubkey]?: Map<string, T> };
  addItem: (
    key: Pubkey,
    item: T,
    options?: { skipSortOnItemUpdate?: boolean; mergeItem?: boolean }
  ) => void;
  removeItem: (key: Pubkey, id: string) => void;
  reset: (key?: Pubkey) => void;
  setData: ({
    key,
    items,
    timeField,
    resetPreviousData,
  }: {
    key: Pubkey;
    items: T[];
    timeField?: keyof T;
    resetPreviousData?: boolean;
  }) => void;
};

export type WebsocketSlices =
  | "xray_new_pair"
  | "about_to_graduate"
  | "xray_graduated"
  | "trades"
  | "market_data"
  | "ohlcv"
  | "holders"
  | "filtered_holders"
  | "traders"
  | "token_events"
  | "live_trades"
  | "discovery_trending_list"
  | "watchlist"
  | "wt_summary"
  | "notifications"
  | "ticker_tape"
  | "warzone_pinned_tokens"
  | "active_positions"
  | "global_active_positions"
  | "pnl"
  | "sentinel_alerts";

export type SliceNameToMessage = {
  xray_new_pair: XRayNewPairMessage;
  about_to_graduate: XRayAboutToGraduateMessage;
  xray_graduated: XRayGraduatedMessage;
  trending: TrendingMessage;
  trades: TradeMessage;
  market_data: MarketDataMessage;
  ohlcv: OHLCVMessage;
  holders: HoldersMessage;
  filtered_holders: HoldersMessage;
  traders: TopTradersMessage;
  token_events: TokenEventMessage;
  discovery_trending_list: DiscoveryTrendingListTokenData;

