import { TableEmpty } from "@/components/table/table-empty";
import { TablePagination } from "@/components/table/table-pagination";
import { usePauseOnHover } from "@/hooks/usePauseOnHover";
import { useTableAutoLoad } from "@/hooks/useTableAutoLoad";
import { cn } from "@/lib/utils";
import { TableExternalStoreKey } from "@/store/persistentStore/slices/tables-store/types";
import { usePersistentGlobalStore } from "@/store/persistentStore/usePersistentGlobalStore";
import { useOptionalDragState } from "@/ui/multiwallet/components/drag-and-drop/dnd-provider";
import {
  ColumnDef,
  ColumnFiltersState,
  ColumnOrderState,
  CoreOptions,
  getCoreRowModel,
  getExpandedRowModel,
  getFilteredRowModel,
  getPaginationRowModel,
  getSortedRowModel,
  OnChangeFn,
  PaginationState,
  Row,
  SortingState,
  Updater,
  useReactTable,
  VisibilityState,
  Header,
  Cell,
  type Table as ReactTable,
} from "@tanstack/react-table";
import { useVirtualizer } from "@tanstack/react-virtual";
import React, {
  useCallback,
  useEffect,
  useMemo,
  useRef,
  useState,
} from "react";
import {
  DndContext,
  DragEndEvent,
  PointerSensor,
  TouchSensor,
  closestCenter,
  useSensor,
  useSensors,
} from "@dnd-kit/core";
import {
  SortableContext,
  verticalListSortingStrategy,
} from "@dnd-kit/sortable";
import {
  restrictToParentElement,
  restrictToVerticalAxis,
} from "@dnd-kit/modifiers";
import { t } from "@lingui/core/macro";
import { useShallow } from "zustand/react/shallow";
import type { TopBarTableMeta } from "@/components/table/types";
import { useMediaQuery, useResizeObserver } from "usehooks-ts";
import { MOBILE_BREAKPOINT } from "@/utils/breakpoints.ts";
import { LoadingSpinner } from "@/components/loading-spinner";
import { DraggableTableRow } from "@/components/table/components/draggable-table-row";
import { VirtualizedTableRow } from "@/components/table/components/virtualized-table-row";
import { MemoizedTableHead } from "@/components/table/components/table-head-cell";
import { MemoizedTableCell } from "@/components/table/components/table-body-cell";
import { SquareSlash } from "lucide-react";
import { ListFilterButton } from "@/ui/base/ListFilterButton.tsx";

type WithExternalStore = {
  storeKey: TableExternalStoreKey;
};

type WithoutExternalStore = {
  storeKey?: undefined;
};

type ExternalStore = WithExternalStore | WithoutExternalStore;

export type TableProps<TData = unknown> = {
  /* eslint-disable  @typescript-eslint/no-explicit-any */
  columns: ColumnDef<TData, any>[];
  data: TData[];
  estimateRowSize: number;
  tableHeadClassName?: React.ComponentProps<"thead">["className"];
  tableCellClassName?: string;
  tableRowClassName?: ((row: Row<TData>) => string) | string;
  emptyTableWrapperClassName?: string;
  enablePagination?: boolean;
  paginationPageSize?: number;
  paginationPageIndex?: number;
  /**
   * Icon to show when there's no data in the table.
   */
  noDataIcon?: React.ReactNode;
  /**
   * Description to show when there's no data in the table.
   */
  noDataDescription?: React.ReactNode;
  /**
   * Action to show below the
   * {@linkcode TableProps.noDataDescription noDataDescription} when there's
   * no data in the table.
   */
  noDataAction?: React.ReactNode;
  /**
   * Icon to show when there *is* data in the table, but no rows match the
   * active filters.
   */
  noFilteredDataIcon?: React.ReactNode;
  /**
   * Description to show when there *is* data in the table, but no rows match
   * the active filters.
   */
  noFilteredDataDescription?: React.ReactNode;
  /**
   * Action to show below the
   * {@linkcode TableProps.noFilteredDataDescription noFilteredDataDescription}
   * when there *is* data in the table, but no rows match the active filters.
   */
  noFilteredDataAction?: React.ReactNode;
  isLoading?: boolean;
  className?: React.ComponentProps<"div">["className"];
  onRowClick?: (
    row: Row<TData>,
    e: React.MouseEvent<HTMLTableRowElement, MouseEvent>
  ) => void;
  enablePause?: boolean;
  hideHeaderOnNoData?: boolean;
  onScrollEnd?: () => void;
  getSubRows?: CoreOptions<TData>["getSubRows"];
  getRowId?: CoreOptions<TData>["getRowId"];
  rowHeight?: "small" | "medium";
  onRowHover?: (rowIndex: number | null) => void;
  highlightedIndex?: number | null;
  columnVisibility?: VisibilityState;
  onColumnVisibilityChange?: OnChangeFn<VisibilityState>;
  columnOrder?: ColumnOrderState;
  onColumnOrderChange?: OnChangeFn<ColumnOrderState>;
  getRowUrl?: (row: Row<TData>) => string;
  overscan?: number;
  topBar?: (params: {
    meta: TopBarTableMeta<TData>;
    isPaused?: boolean;
  }) => React.ReactNode;
  onRowRightClick?: (row: Row<TData>) => void;
  initialSorting?: SortingState;
  tableHeadRowClassName?: string;
  tableHeadCellClassName?: string;
  showPauseIconOnly?: boolean;
  hidePauseIndicator?: boolean;
  isNextPageLoading?: boolean;

