import {
  formatSmallNumber,
  formatValue,
  FormatValueOptions,
} from "@/lib/formatters";
import { cn } from "@/lib/utils";
import NumberFlow from "@number-flow/react";
import React from "react";
import SolanaIcon from "@/assets/icons/solana.svg";

export type Precisions = {
  zeroToOne: number;
  oneToHundred: number;
  hundredToThousand: number;
  tenThousand: number;
  hundredThousand: number;
  million: number;
  billion: number;
};

export type FormattedNumberProps = Omit<
  React.ComponentProps<"span">,
  "prefix" | "suffix"
> & {
  value?: number | string | null;
  preset?: PresetKey;
  showPositiveSign?: boolean;
  showNegativeSign?: boolean;
  showColor?: boolean;
  precisionMode?: boolean;
  customNumberPreset?: Partial<Precisions>;
  prefix?: React.ReactNode | null;
  suffix?: React.ReactNode | null;
  showBrackets?: boolean;
  valueClassName?: string;
  animate?: boolean;
  options?: FormatValueOptions;
  isEstimated?: boolean;
  loader?: React.ReactNode;
  isLoading?: boolean;
  truncate?: boolean;
};

const NUMBER_FORMAT_CONFIGS = {
  DEFAULT: "default",
  USD: "usd",
  SOL: "sol",
  PERCENTAGE: "percentage",
  COMPACT: "compact",
  COMPACT_PERCENTAGE: "compact_percentage",
  PVP: "pvp",
} as const;

export type PresetKey =
  (typeof NUMBER_FORMAT_CONFIGS)[keyof typeof NUMBER_FORMAT_CONFIGS];

const DEFAULT_PRECISIONS: Precisions = {
  zeroToOne: 2,
  oneToHundred: 2,
  hundredToThousand: 2,
  tenThousand: 2, // between 1k and <10k
