import React, { useCallback, useMemo } from "react";
import { Tooltip } from "@/components/tooltip.tsx";
import { Button } from "@/components/button.tsx";
import StarIcon from "@/assets/icons/star.svg";
import { cn } from "@/lib/utils.ts";
import { t } from "@lingui/core/macro";
import { useSetWatchlistItem } from "@/api/watchlist/useSetWatchlistItem.tsx";
import { useDeleteWatchlistToken } from "@/api/watchlist/useDeleteWatchlistToken";
import { useGetWatchlist } from "@/api/watchlist/useGetWatchlist.ts";

type WatchlistToggleProps = {
  mint: string;
  ticker: string;
  disableTooltip?: boolean;
};

export const WatchlistToggle = ({
  mint,
  ticker,
  disableTooltip,
}: WatchlistToggleProps) => {
  const { data: watchlistTokens } = useGetWatchlist();
  const { mutate: addWatchlistItem, isPending: isPendingAdd } =
    useSetWatchlistItem();
  const { mutate: deleteWatchlistItem, isPending: isPendingDelete } =
    useDeleteWatchlistToken();

  const isInWatchlist = useMemo(
    () =>
      !!watchlistTokens?.watchlistTokens?.some(group =>
        group.watchlistTokens?.some(token => token.tokenMint === mint)
      ),
    [watchlistTokens, mint]
  );

  const handleToggleWatchlistItem = useCallback(
    (e: React.MouseEvent) => {
      e.preventDefault();
      e.stopPropagation();

      if (isPendingAdd || isPendingDelete) return;

      if (isInWatchlist) {
        deleteWatchlistItem({ mint, symbol: ticker });
      } else {
        addWatchlistItem({ tokenMint: mint, tokenSymbol: ticker });
      }
    },
    [
      isInWatchlist,
      mint,
      ticker,
      addWatchlistItem,
      deleteWatchlistItem,
      isPendingDelete,
      isPendingAdd,
    ]
  );

  const buttonContent = (
    <Button
      data-testid="watchlist-toggle-button"
      variant="text"
      onClick={handleToggleWatchlistItem}
      className={cn(
        "[&_path]:stroke-fg-secondary hover:[&_path]:stroke-fg-primary flex size-4 items-center justify-center overflow-hidden",
        isInWatchlist &&
          "[&_svg]:fill-warning [&_path]:stroke-warning hover:[&_path]:stroke-warning",
        isPendingAdd || isPendingDelete
          ? "disabled:cursor-wait disabled:bg-transparent [&_svg]:opacity-50"
          : ""
      )}
      disabled={isPendingAdd || isPendingDelete}
    >
      <StarIcon
        className="transition-colors"
        width={16}
        height={16}
      />
    </Button>
  );

  if (disableTooltip) {
    return buttonContent;
  }

  return (
    <Tooltip
      content={
        isInWatchlist
          ? t`Remove ${ticker} from Watchlist`
          : t`Add ${ticker} to Watchlist`
      }
      triggerClassName={cn(
        "[&_path]:stroke-fg-secondary hover:[&_path]:stroke-fg-primary flex size-4 items-center justify-center overflow-hidden",
        isInWatchlist &&
          "[&_svg]:fill-warning [&_path]:stroke-warning hover:[&_path]:stroke-warning"
      )}
      side="right"
      align="center"
      className="px-2 py-1"
      arrowClassName="w-2"
      sideOffset={2}
    >
      {buttonContent}
    </Tooltip>
  );
};

