export const calculatePriceChange = (
  currentPrice: number | undefined,
  earliestPrice: number | undefined
) => {
  if (!currentPrice || !earliestPrice || earliestPrice === 0) return 0;

  return Number(
    (((currentPrice - earliestPrice) / earliestPrice) * 100).toPrecision(5)
  );
};

export const nanSafeSort = (a: number, b: number) => {
  const aVal = isNaN(a) ? 0 : a;
  const bVal = isNaN(b) ? 0 : b;

  return aVal - bVal;
};
