    81→
    82→// Scan Countdown Component - shows live countdown to next scan
    83→const ScanCountdown = ({
    84→  scanTimestamp,
    85→  intervalMs,
    86→}: {
    87→  scanTimestamp: number | null;
    88→  intervalMs: number;
    89→}) => {
    90→  const [countdown, setCountdown] = useState<number | null>(null);
    91→
    92→  useEffect(() => {
    93→    if (scanTimestamp === null) {
    94→      setCountdown(null);
    95→      return;
    96→    }
    97→
    98→    const calculateCountdown = () => {
    99→      const nextScanTime = scanTimestamp + intervalMs;
   100→      const remaining = Math.round((nextScanTime - Date.now()) / 1000);
   101→      return remaining;
   102→    };
   103→
   104→    // Initial calculation
   105→    setCountdown(calculateCountdown());
   106→
   107→    // Update every second
   108→    const intervalId = setInterval(() => {
   109→      setCountdown(calculateCountdown());
   110→    }, 1000);
   111→
   112→    return () => clearInterval(intervalId);
   113→  }, [scanTimestamp, intervalMs]);
   114→
   115→  if (scanTimestamp === null) {
   116→    return <span className="text-text-tertiary text-[10px]">Waiting...</span>;
   117→  }
   118→
   119→  if (countdown === null) {
   120→    return <span className="text-text-tertiary text-[10px]">Waiting...</span>;
   121→  }
   122→
   123→  if (countdown <= 0) {
   124→    return <span className="text-[10px] text-cyan-400">Scanning...</span>;
   125→  }
   126→
   127→  return (
   128→    <span className="text-text-secondary text-[10px]">Next: {countdown}s</span>
   129→  );
   130→};
