   132→// Position Card Component - handles nullable P&L from NATS market data
   133→const PositionCard = ({ position }: { position: AutopilotPositionWithPnL }) => {
   134→  const isProfit = (position.pnlPercent ?? 0) >= 0;
   135→  const holdTime = Math.floor((Date.now() - position.entryTime) / 60000); // minutes
   136→  const hasMarketData = position.pnlPercent !== null;
   137→
   138→  return (
   139→    <div className="border-border-primary bg-bg-tertiary/50 flex items-center justify-between rounded-lg border p-3">
   140→      <div className="flex items-center gap-3">
   141→        <div
   142→          className={cn(
   143→            "flex h-8 w-8 items-center justify-center rounded-lg text-xs font-bold",
   144→            hasMarketData
   145→              ? isProfit
   146→                ? "bg-green-500/20 text-green-400"
   147→                : "bg-red-500/20 text-red-400"
   148→              : "bg-gray-500/20 text-gray-400"
   149→          )}
   150→        >
   151→          {hasMarketData ? (
   152→            isProfit ? (
   153→              <TrendingUp className="h-4 w-4" />
   154→            ) : (
   155→              <TrendingDown className="h-4 w-4" />
   156→            )
   157→          ) : (
   158→            <Activity className="h-4 w-4 animate-pulse" />
   159→          )}
   160→        </div>
   161→        <div>
   162→          <span className="text-text-primary text-sm font-medium">
   163→            ${position.symbol}
   164→          </span>
   165→          <div className="text-text-secondary flex items-center gap-1 text-[10px]">
   166→            <Clock className="h-3 w-3" />
   167→            {holdTime}m
   168→          </div>
   169→        </div>
   170→      </div>
   171→      <div className="text-right">
   172→        {hasMarketData ? (
   173→          <>
   174→            <div
   175→              className={cn(
   176→                "text-sm font-semibold",
   177→                isProfit ? "text-green-400" : "text-red-400"
   178→              )}
   179→            >
   180→              {isProfit ? "+" : ""}
   181→              {position.pnlPercent!.toFixed(2)}%
   182→            </div>
   183→            <div className="text-text-secondary text-[10px]">
   184→              {position.positionSizeSol.toFixed(2)} SOL
   185→            </div>
   186→          </>
   187→        ) : (
   188→          <>
   189→            <div className="text-text-tertiary animate-pulse text-sm font-semibold">
   190→              Loading...
   191→            </div>
   192→            <div className="text-text-secondary text-[10px]">
   193→              {position.positionSizeSol.toFixed(2)} SOL
   194→            </div>
   195→          </>
   196→        )}
   197→      </div>
   198→    </div>
   199→  );
   200→};
