   466→
   467→// Strategies Panel Component
   468→const StrategiesPanel = ({
   469→  strategies,
   470→  sendCommand,
   471→  isConnected,
   472→}: {
   473→  strategies: AutopilotStrategies;
   474→  sendCommand: (cmd: AutopilotCommand) => void;
   475→  isConnected: boolean;
   476→}) => {
   477→  const [showStrategies, setShowStrategies] = useState(false);
