   479→
   480→  return (
   481→    <div className="border-border-primary bg-bg-tertiary/30 rounded-lg border">
   482→      {/* Header - always visible */}
   483→      <button
   484→        onClick={() => setShowStrategies(!showStrategies)}
   485→        className="flex w-full items-center justify-between px-3 py-2 text-xs"
   486→        disabled={!isConnected}
   487→      >
   488→        <div className="flex items-center gap-2">
   489→          <Layers className="text-text-secondary h-3.5 w-3.5" />
   490→          <span className="text-text-secondary font-medium">Strategies</span>
   491→        </div>
   492→        <div className="flex items-center gap-2">
