   535→                >
   536→                  <div className="mt-0.5 flex-shrink-0">
   537→                    {isActive ? (
   538→                      <CheckCircle2 className="h-4 w-4 text-green-400" />
   539→                    ) : (
   540→                      <Circle className="text-text-tertiary h-4 w-4" />
   541→                    )}
   542→                  </div>
   543→                  <div className="min-w-0 flex-1">
   544→                    <div className="flex items-center gap-2">
   545→                      <span
   546→                        className={cn(
   547→                          "text-xs font-medium",
   548→                          isActive ? "text-green-400" : "text-text-primary"
   549→                        )}
   550→                      >
   551→                        {strategy.name}
   552→                      </span>
   553→                      <span
   554→                        className={cn(
   555→                          "rounded px-1 py-0.5 text-[9px]",
   556→                          strategy.mode === "all"
   557→                            ? "bg-blue-500/20 text-blue-400"
   558→                            : "bg-purple-500/20 text-purple-400"
   559→                        )}
   560→                      >
   561→                        {strategy.mode.toUpperCase()}
   562→                      </span>
   563→                    </div>
   564→                    <div className="text-text-tertiary truncate text-[10px]">
   565→                      {strategy.summary}
   566→                    </div>
   567→                  </div>
   568→                </button>
   569→              );
   570→            })
   571→          )}
   572→        </div>
   573→      )}
   574→    </div>
   575→  );
   576→};
   577→
   578→// Stats Panel Component
   579→const StatsPanel = ({ stats }: { stats: AutopilotStats }) => {
   580→  const isProfit = stats.totalPnlSol >= 0;
   581→
   582→  return (
   583→    <div className="border-border-primary bg-bg-tertiary/30 grid grid-cols-4 gap-2 rounded-lg border p-3">
   584→      <div className="text-center">
   585→        <div className="text-text-primary text-lg font-bold">
   586→          {stats.closedTrades}
   587→        </div>
   588→        <div className="text-text-secondary text-[10px]">Trades</div>
   589→      </div>
   590→      <div className="text-center">
   591→        <div
   592→          className={cn(
   593→            "text-lg font-bold",
   594→            stats.winRate >= 0.5 ? "text-green-400" : "text-red-400"
