   596→        >
   597→          {(stats.winRate * 100).toFixed(0)}%
   598→        </div>
   599→        <div className="text-text-secondary text-[10px]">Win Rate</div>
   600→      </div>
   601→      <div className="text-center">
   602→        <div className="text-text-primary text-lg font-bold">
   603→          {stats.wins}W/{stats.losses}L
   604→        </div>
   605→        <div className="text-text-secondary text-[10px]">Record</div>
   606→      </div>
   607→      <div className="text-center">
   608→        <div
   609→          className={cn(
   610→            "text-lg font-bold",
   611→            isProfit ? "text-green-400" : "text-red-400"
   612→          )}
   613→        >
   614→          {isProfit ? "+" : ""}
   615→          {stats.totalPnlSol.toFixed(2)}
   616→        </div>
   617→        <div className="text-text-secondary text-[10px]">P&L (SOL)</div>
   618→      </div>
   619→    </div>
   620→  );
   621→};
   622→
   623→// Last Analysis Row Component
   624→const LastAnalysisRow = ({
   625→  analysis,
   626→}: {
   627→  analysis: {
   628→    symbol: string;
   629→    timestamp: number;
   630→    priceChange5m: number | null;
   631→    priceChange1h: number | null;
   632→    buySellRatio: number | null;
   633→    rulesChecked: number;
   634→    rulesMatched: number;
   635→    outcome: string;
   636→    reason: string;
   637→  } | null;
   638→}) => {
   639→  // Use live-updating time ago hook
   640→  const timeStr = useTimeAgo(analysis?.timestamp);
   641→
   642→  if (!analysis) return null;
   643→
   644→  const outcomeConfig = {
   645→    no_signal: { color: "text-gray-400", bg: "bg-gray-500/10" },
   646→    buy_signal: { color: "text-green-400", bg: "bg-green-500/10" },
   647→    sell_signal: { color: "text-red-400", bg: "bg-red-500/10" },
   648→    skipped: { color: "text-yellow-400", bg: "bg-yellow-500/10" },
   649→  };
   650→
   651→  const config =
   652→    outcomeConfig[analysis.outcome as keyof typeof outcomeConfig] ||
   653→    outcomeConfig.no_signal;
   654→
   655→  return (
   656→    <div
   657→      className={cn("border-border-primary rounded-lg border p-2", config.bg)}
   658→    >
   659→      <div className="flex items-center justify-between">
   660→        <div className="flex items-center gap-2">
   661→          <Activity className={cn("h-3.5 w-3.5", config.color)} />
   662→          <span className="text-text-primary text-xs font-medium">
   663→            ${analysis.symbol}
   664→          </span>
   665→          <span className="text-text-tertiary text-[10px]">{timeStr}</span>
   666→        </div>
   667→        <div className="flex items-center gap-2">
   668→          <span className={cn("text-[10px] font-semibold", config.color)}>
   669→            {analysis.rulesMatched}/{analysis.rulesChecked}
   670→          </span>
   671→        </div>
   672→      </div>
   673→      <div className="mt-1 flex items-center gap-3 text-[10px]">
   674→        {analysis.priceChange5m !== null && (
   675→          <span
   676→            className={
   677→              analysis.priceChange5m >= 0 ? "text-green-400" : "text-red-400"
   678→            }
   679→          >
   680→            5m: {analysis.priceChange5m >= 0 ? "+" : ""}
   681→            {analysis.priceChange5m.toFixed(2)}%
   682→          </span>
   683→        )}
   684→        {analysis.priceChange1h !== null && (
   685→          <span
   686→            className={
   687→              analysis.priceChange1h >= 0 ? "text-green-400" : "text-red-400"
   688→            }
   689→          >
   690→            1h: {analysis.priceChange1h >= 0 ? "+" : ""}
   691→            {analysis.priceChange1h.toFixed(2)}%
   692→          </span>
   693→        )}
   694→        {analysis.buySellRatio !== null && (
   695→          <span className="text-text-secondary">
   696→            B/S: {analysis.buySellRatio.toFixed(1)}:1
   697→          </span>
   698→        )}
   699→      </div>
   700→      <div className="text-text-tertiary mt-1 truncate text-[9px]">
   701→        {analysis.reason}
   702→      </div>
   703→    </div>
   704→  );
   705→};
   706→
   707→// Section Header Component
   708→const SectionHeader = ({ children }: { children: React.ReactNode }) => (
   709→  <div className="text-text-tertiary mb-1.5 text-[10px] font-medium tracking-wide uppercase">
   710→    {children}
   711→  </div>
   712→);
   713→
   714→// Collapsible Section Component - follows ControlsPanel/StrategiesPanel pattern
   715→const CollapsibleSection = ({
   716→  title,
   717→  defaultExpanded = true,
   718→  children,
   719→}: {
   720→  title: string;
   721→  defaultExpanded?: boolean;
   722→  children: React.ReactNode;
   723→}) => {
   724→  const [expanded, setExpanded] = useState(defaultExpanded);
   725→
   726→  return (
   727→    <div
   728→      className={cn(
   729→        "flex flex-shrink-0 flex-col transition-opacity duration-200",
   730→        !expanded && "opacity-75"
   731→      )}
   732→    >
   733→      <button
   734→        onClick={() => setExpanded(!expanded)}
   735→        className={cn(
   736→          "flex cursor-pointer items-center gap-1.5 text-[10px] font-medium tracking-wide uppercase transition-colors",
   737→          expanded
   738→            ? "text-text-tertiary hover:text-text-secondary mb-1.5"
   739→            : "text-text-tertiary/80 hover:text-text-secondary hover:border-border-primary/30 hover:bg-bg-tertiary/30 rounded border border-transparent px-1.5 py-1"
   740→        )}
   741→      >
   742→        <span
   743→          className={cn(
   744→            "text-[8px] transition-transform duration-200",
   745→            expanded ? "rotate-0" : "-rotate-90"
   746→          )}
   747→        >
   748→          ▼
   749→        </span>
   750→        {title}
   751→        {!expanded && (
   752→          <span className="text-text-tertiary/60 ml-auto text-[8px] font-normal normal-case">
   753→            click to expand
   754→          </span>
   755→        )}
   756→      </button>
   757→      <div
   758→        className={cn(
   759→          "grid transition-all duration-200",
   760→          expanded ? "grid-rows-[1fr] opacity-100" : "grid-rows-[0fr] opacity-0"
   761→        )}
   762→      >
   763→        <div className="overflow-hidden">
   764→          <div className="flex flex-col gap-2">{children}</div>
   765→        </div>
   766→      </div>
   767→    </div>
   768→  );
   769→};
   770→
   771→// Main Autopilot Widget Component
   772→export const AutopilotWidget = () => {
   773→  // Connect to autopilot backend and auto-subscribe to NATS market data
   774→  useAutopilotSubscription({ isEnabled: true });
   775→
   776→  // Get all autopilot data from Zustand store
   777→  const {
   778→    positions,
   779→    signals,
   780→    marketStatus,
   781→    stats,
   782→    scanTick,
   783→    lastAnalysis,
   784→    tokenList,
   785→    config,
   786→    strategies,
   787→    isConnected,
   788→    sendCommand,
   789→  } = useAutopilotData();
   790→
   791→  const totalTokens =
   792→    tokenList.watchlist.length +
   793→    tokenList.trending.length +
   794→    tokenList.whales.length;
   795→
   796→  return (
   797→    <div className="flex h-full flex-col gap-4 overflow-hidden">
   798→      {/* Header - Market Status */}
   799→      <div className="flex items-center justify-between">
   800→        <div className="flex items-center gap-2">
   801→          <Bot
   802→            className={cn(
   803→              "h-5 w-5",
   804→              isConnected ? "text-green-400" : "text-red-400"
   805→            )}
   806→          />
   807→          <span className="text-text-primary text-sm font-semibold">
   808→            Token Autopilot
   809→          </span>
   810→          {scanTick && (
   811→            <span className="text-text-secondary text-[10px]">
   812→              #{scanTick.scanNumber} | {totalTokens} tokens
   813→            </span>
   814→          )}
   815→          {isConnected && (
   816→            <>
   817→              <span className="text-text-tertiary text-[10px]">|</span>
   818→              <ScanCountdown
   819→                scanTimestamp={scanTick?.timestamp ?? null}
   820→                intervalMs={config.pollIntervalMs}
   821→              />
   822→            </>
   823→          )}
   824→        </div>
   825→        <MarketStatusBadge status={marketStatus} />
   826→      </div>
   827→
   828→      {/* Connection Status - Always visible when disconnected */}
   829→      {!isConnected && (
   830→        <div className="flex items-center gap-2 rounded-lg border border-yellow-500/30 bg-yellow-500/10 px-3 py-2">
   831→          <AlertTriangle className="h-4 w-4 text-yellow-400" />
   832→          <span className="text-xs text-yellow-400">
   833→            Connecting to autopilot backend...
   834→          </span>
   835→        </div>
   836→      )}
   837→
   838→      {/* ═══ PERFORMANCE SECTION ═══ */}
   839→      <CollapsibleSection
   840→        title="Performance"
   841→        defaultExpanded={true}
   842→      >
   843→        <StatsPanel stats={stats} />
   844→
   845→        {/* Open Positions */}
   846→        {positions.length > 0 && (
   847→          <div>
   848→            <div className="text-text-secondary mb-1.5 flex items-center gap-2 text-xs font-medium">
   849→              <Target className="h-3.5 w-3.5" />
   850→              Open Positions ({positions.length})
   851→            </div>
   852→            <div className="max-h-40 space-y-2 overflow-auto">
   853→              {positions.map(pos => (
   854→                <PositionCard
   855→                  key={pos.mint}
   856→                  position={pos}
   857→                />
   858→              ))}
   859→            </div>
   860→          </div>
   861→        )}
   862→      </CollapsibleSection>
   863→
   864→      {/* ═══ ACTIVITY SECTION ═══ */}
   865→      <CollapsibleSection
   866→        title="Activity"
   867→        defaultExpanded={true}
   868→      >
   869→        <LastAnalysisRow analysis={lastAnalysis} />
   870→
   871→        {/* Recent Signals */}
   872→        {signals.length > 0 && (
   873→          <div>
   874→            <div className="text-text-secondary mb-1.5 flex items-center gap-2 text-xs font-medium">
   875→              <Zap className="h-3.5 w-3.5" />
   876→              Recent Signals
   877→            </div>
   878→            <div className="max-h-24 overflow-auto">
   879→              <div className="space-y-0">
   880→                {signals.slice(0, 5).map((signal, idx) => (
   881→                  <SignalItem
   882→                    key={`${signal.timestamp}-${idx}`}
   883→                    signal={signal}
   884→                  />
   885→                ))}
   886→              </div>
   887→            </div>
   888→          </div>
   889→        )}
   890→      </CollapsibleSection>
   891→
   892→      {/* ═══ TOKEN WATCH SECTION ═══ (Main scrollable area) */}
   893→      <div className="flex min-h-0 flex-1 flex-col gap-2 overflow-auto">
   894→        <SectionHeader>Tokens</SectionHeader>
   895→
   896→        {/* Watchlist */}
   897→        <TokenListSection
   898→          title="Watchlist"
   899→          icon={Eye}
