   911→
   912→        {/* Whale Moves */}
   913→        <TokenListSection
   914→          title="Whale Moves"
   915→          icon={Wallet}
   916→          tokens={tokenList.whales}
   917→          iconColor="text-green-400"
   918→        />
   919→
   920→        {/* Empty state if no tokens */}
   921→        {totalTokens === 0 && isConnected && (
   922→          <div className="text-text-secondary border-border-primary/50 rounded-lg border border-dashed p-4 text-center text-xs">
   923→            No tokens being watched. Enable auto-sources or add to watchlist.
