@layer base {
  *,
  ::after,
  ::before,
  ::backdrop,
  ::file-selector-button {
    border-color: var(--color-gray-200, currentColor);
  }
  button,
  button:not(:disabled),
  [role="button"]:not(:disabled) {
    cursor: pointer;
  }

  html {
    text-rendering: geometricPrecision;
    @apply antialiased;
  }
  body {
    @apply text-text-primary bg-bg-primary;
  }

  svg {
    @apply shrink-0;
  }
  img {
    @apply shrink-0;
  }

  * {
    @apply default-scrollbar;
  }
}

@utility writing-* {
  writing-mode: --value(
    "horizontal",
    "vertical-rl",
    "vertical-lr",
    "sideways-rl",
    "sideways-lr"
  );
}

@utility default-scrollbar {
  @apply scrollbar-thumb-text-disabled scrollbar-track-bg-secondary-hover scrollbar scrollbar-w-1.5 scrollbar-h-1.5 scrollbar-track-rounded scrollbar-thumb-rounded-full;
}

@utility hide-scrollbar {
  -ms-overflow-style: none; /* IE and Edge */
  scrollbar-width: none; /* Firefox */

  &::-webkit-scrollbar {
    display: none; /* Chrome, Safari, Edge */
  }
}

@layer utilities {
  .highlighted {
    text-shadow: 0px 0px 20px;
    svg {
      filter: drop-shadow(0px 0px 20px);
    }
  }
  .border-animation {
    @apply relative overflow-hidden;
  }
  /* Gradient */
  .border-animation::before {
    background: conic-gradient(
      transparent 0deg,
      var(--lightblue-950) 40deg,
      transparent 80deg
    );

    @apply animate-border-spin absolute top-1/2 left-1/2 h-[175%] w-[175%] -translate-x-1/2 -translate-y-1/2 content-[''];
  }

  .font-oswald-italic {
    font-family: "Oswald", sans-serif;
    transform: skewX(-5deg) translateX(0.1rem);
    display: inline-block;
  }
}

input:-webkit-autofill,
input:-webkit-autofill:focus,
input:-webkit-autofill:hover,
input:-webkit-autofill:active {
  -webkit-box-shadow: none !important; /* Twój kolor tła */
  box-shadow: none !important; /* Twój kolor tła */
  -webkit-text-fill-color: var(
    --color-gray-50
  ) !important; /* Twój kolor tekstu */
  color: var(--color-gray-50) !important;
  transition: background-color 5000s ease-in-out 0s;
}

em-emoji-picker {
  height: 250px;
}

.no-radius .apply-radius-reset {
  border-radius: 0 !important;
}

[data-capturing="true"] .hide-during-screenshot {
  display: none;
}

[data-capturing="true"] .hide-shadow {
  box-shadow: none;
}

.custom-pagination {
  display: flex;
  justify-content: center;
  gap: 8px;
  margin-top: 1rem;
}

.custom-pagination .swiper-pagination-bullet {
  width: 8px;
  height: 8px;
  background-color: rgba(255, 255, 255, 0.35);
  border-radius: 9999px;
  opacity: 1;
  transition: all 0.3s ease-in-out;
}

.custom-pagination .swiper-pagination-bullet-active {
  width: 24px;
  height: 8px;
  background-color: rgba(255, 255, 255, 0.9);
  border-radius: 9999px;
  box-shadow: 0 0 6px rgba(255, 255, 255, 0.6);
  transition: all 0.3s ease-in-out;
}

@keyframes xrayElementAppear {
  0% {
    background-color: transparent;
  }
  50% {
    background-color: #24282d;
  }
  100% {
    background-color: transparent;
  }
}

/* Widget glow effects for new data */
@keyframes widgetGlow {
  0% {
    box-shadow: 0 0 0 0 rgba(74, 222, 128, 0.4);
  }
  50% {
    box-shadow: 0 0 20px 4px rgba(74, 222, 128, 0.2);
  }
  100% {
    box-shadow: 0 0 0 0 rgba(74, 222, 128, 0);
  }
}

@keyframes widgetGlowWarning {
  0% {
    box-shadow: 0 0 0 0 rgba(251, 191, 36, 0.4);
  }
  50% {
    box-shadow: 0 0 20px 4px rgba(251, 191, 36, 0.2);
  }
  100% {
    box-shadow: 0 0 0 0 rgba(251, 191, 36, 0);
  }
}

/* PvP Live indicator pulse */
@keyframes livePulse {
  0%,
  100% {
    opacity: 1;
    transform: scale(1);
  }
  50% {
    opacity: 0.5;
    transform: scale(1.1);
  }
}

.widget-glow {
  animation: widgetGlow 0.6s ease-out;
}

.widget-glow-warning {
  animation: widgetGlowWarning 0.6s ease-out;
}

.live-pulse {
  animation: livePulse 2s ease-in-out infinite;
}

.dotted-landscape-shadow {
  box-shadow: 0 5px 4px rgba(21, 23, 25, 0.3);
}

.dotted-portrait-shadow {
  box-shadow: 0 5px 5px rgba(21, 23, 25, 0.2);
}

.logo-svg {
  filter: drop-shadow(0 1px 4px rgba(0, 0, 0, 1));
  overflow: visible;
  display: block;
}

.xray-element-appear {
  animation: xrayElementAppear 0.6s ease-in-out;
}

/* https://github.com/barvian/number-flow/issues/84#issuecomment-2545598793 */
number-flow-react::part(left),
number-flow-react::part(right),
number-flow-react::part(left)::after,
number-flow-react::part(right)::after,
number-flow-react::part(symbol) {
  padding: calc(var(--number-flow-mask-height, 0.25em) / 2) 0;
}

/* View Transitions */
@keyframes view-fade-in {
  from {
    opacity: 0;
  }
}

@keyframes view-fade-out {
  to {
    opacity: 0;
  }
}

@keyframes slide-from-right {
  from {
    transform: translateX(30px);
  }
}

@keyframes slide-to-left {
  to {
    transform: translateX(-30px);
  }
}

/* Glitch distortion animation for view transitions */
@keyframes glitch-out {
  0% {
    filter: none;
    opacity: 1;
    transform: translateX(0);
  }
  20% {
    filter: url(#glitch-distortion);
    opacity: 1;
    transform: translateX(-2px);
  }
  40% {
    filter: url(#glitch-distortion);
    opacity: 0.9;
    transform: translateX(4px);
  }
  60% {
    filter: url(#glitch-distortion);
    opacity: 0.7;
    transform: translateX(-3px);
  }
  80% {
    filter: url(#glitch-distortion) blur(2px);
    opacity: 0.4;
    transform: translateX(2px);
  }
  100% {
    filter: url(#glitch-distortion) blur(4px);
    opacity: 0;
    transform: translateX(-20px);
  }
}

@keyframes glitch-in {
  0% {
    filter: url(#glitch-distortion) blur(4px);
    opacity: 0;
    transform: translateX(20px);
  }
  20% {
    filter: url(#glitch-distortion) blur(2px);
    opacity: 0.4;
    transform: translateX(-2px);
  }
  40% {
    filter: url(#glitch-distortion);
    opacity: 0.7;
    transform: translateX(3px);
  }
  60% {
    filter: url(#glitch-distortion);
    opacity: 0.9;
    transform: translateX(-4px);
  }
  80% {
    filter: url(#glitch-distortion);
    opacity: 1;
    transform: translateX(2px);
  }
  100% {
    filter: none;
    opacity: 1;
    transform: translateX(0);
  }
}

::view-transition-old(root) {
  animation: 350ms cubic-bezier(0.4, 0, 0.6, 1) both glitch-out;
}

::view-transition-new(root) {
  animation: 350ms cubic-bezier(0.4, 0, 0.2, 1) both glitch-in;
}

/* PvP animated border angle property */
@property --border-angle {
  inherits: false;
  initial-value: 0deg;
  syntax: "<angle>";
}

