import { JsonData } from "@/base/jsonData";
import { MessageStatus } from "@/types/websocket.model";

export interface TopicRegistry {
  /*
   * DO NOT modify this interface directly - use declaration merging instead!

   * To add a new topic, in your topic file, add:
   *
   * ```typescript
   * declare module "@/websocket/topic/types" {
   *   interface TopicRegistry {
   *     market_data: {
   *       name: `market_data.${Pubkey}`;
   *       args: MyTopicArgs;
   *   }
   * }
   * ```
   */
}
export type TopicName = TopicRegistry[keyof TopicRegistry]["name"];

/**
 * Extracts the args for a topic, eg:
 * TopicArgsFor<"market_data.123"> is { nats_replay: boolean }
 * TopicArgsFor<"sol_price"> is undefined
 */
export type TopicArgs<T extends TopicName> = {
  [K in keyof TopicRegistry]: TopicRegistry[K]["name"] extends T
    ? TopicRegistry[K]["args"]
    : undefined;
}[keyof TopicRegistry];

/**
 * Message type for topic subscription acknowledgements.
 */
export type TopicSubscribeAckMessage = {
  id: number;
  status: 2;
  message: {
    topic: TopicName;
  };
};

export type TopicUnsubscribeAckMessage = {
  id: number;
  status: 3;
  message: {
    topic: TopicName;
  };
};

/**
 * Message type for topic data messages.
 */
export interface WebsocketJsonDataMessage<MessageData = JsonData> {
  id: number;
  message: MessageData;
  status: MessageStatus;
}

