     3 *
     4 * Main hook for accessing autopilot data from the Zustand store.
     5 * Provides all autopilot data plus a sendCommand function for controls.
     6 *
     7 * Returns derived stats that compute totalPnlSol from live position data
     8 * instead of using the static backend value.
     9 *
    10 * Also computes SOL price change percentage from NATS data for market status.
    11 */
    12
    13import { useMemo, useRef, useEffect } from "react";
    14import { useGlobalStore, useShallow } from "@/store/memoryStore/useGlobalStore";
    15import { useAutopilotPositions } from "./useAutopilotPositions";
    16import { useAutopilotTokensWithPrices } from "./useAutopilotTokensWithPrices";
    17import { autopilotWebsocket } from "@/websocket/autopilot/autopilotWebsocketInstance";
    18import type { AutopilotCommand } from "@/websocket/autopilot/AutopilotWebsocket";
    19
    20/**
    21 * Main hook for accessing all autopilot data.
    22 * Returns positions with live P&L, signals, stats, and controls.
    23 */
    24export const useAutopilotData = () => {
    25  // Positions with live P&L from NATS market data
    26  const positions = useAutopilotPositions();
    27
    28  // Token lists with live prices from NATS market data
    29  const tokenListWithPrices = useAutopilotTokensWithPrices();
    30
    31  // Calculate unrealized P&L from open positions (real-time from NATS market data)
    32  const unrealizedPnlSol = useMemo(() => {
    33    return positions.reduce((sum, pos) => sum + (pos.pnlSol ?? 0), 0);
    34  }, [positions]);
    35
    36  // Get SOL price from store (from sol_price NATS topic)
    37  const solPriceUsdStr = useGlobalStore(state => state.solanaPrice.usd);
    38  const storeSolPrice = solPriceUsdStr ? parseFloat(solPriceUsdStr) : null;
    39
    40  // Get market data map to derive SOL price from token prices
    41  const marketDataMap = useGlobalStore(
    42    useShallow(state => state.websocketMessages.market_data.data)
    43  );
    44
    45  // Derive SOL price from NATS market data (price_usd / price_sol)
    46  const derivedSolPrice = useMemo(() => {
    47    // Get first token with valid prices from market data
    48    for (const mintData of Object.values(marketDataMap)) {
    49      if (mintData && mintData.size > 0) {
    50        const tokenData = Array.from(mintData.values())[0];
    51        if (tokenData?.price_usd && tokenData?.price_sol) {
    52          const priceUsd =
    53            typeof tokenData.price_usd === "string"
    54              ? parseFloat(tokenData.price_usd)
    55              : tokenData.price_usd;
    56          const priceSol =
    57            typeof tokenData.price_sol === "string"
    58              ? parseFloat(tokenData.price_sol)
    59              : tokenData.price_sol;
    60
    61          if (priceSol > 0 && !isNaN(priceUsd) && !isNaN(priceSol)) {
    62            return priceUsd / priceSol;
    63          }
    64        }
    65      }
    66    }
    67    return null;
    68  }, [marketDataMap]);
    69
    70  // Priority: store (sol_price topic) > derived from market data
    71  const solPriceUsd = storeSolPrice ?? derivedSolPrice;
    72
    73  // DIAGNOSTIC: Log SOL price source
    74  console.log("[Autopilot] SOL price:", {
    75    storeSolPrice,
    76    derivedSolPrice,
    77    solPriceUsd,
    78  });
    79
    80  // Track initial SOL price to calculate change since widget opened
    81  const initialSolPriceRef = useRef<number | null>(null);
    82
    83  useEffect(() => {
    84    if (
    85      solPriceUsd &&
    86      !isNaN(solPriceUsd) &&
    87      initialSolPriceRef.current === null
    88    ) {
    89      initialSolPriceRef.current = solPriceUsd;
    90    }
    91  }, [solPriceUsd]);
    92
    93  // Calculate SOL price change percentage
    94  const solPriceChangePercent = useMemo(() => {
    95    if (!solPriceUsd || isNaN(solPriceUsd) || !initialSolPriceRef.current)
    96      return 0;
    97    return (
    98      ((solPriceUsd - initialSolPriceRef.current) /
    99        initialSolPriceRef.current) *
   100      100
   101    );
   102  }, [solPriceUsd]);
   103
   104  // All other data from autopilot store (tokenList now comes from useAutopilotTokensWithPrices)
   105  const {
   106    isConnected,
   107    signals,
   108    marketStatus,
   109    stats,
   110    scanTick,
   111    lastAnalysis,
   112    config,
   113    strategies,
   114  } = useGlobalStore(
   115    useShallow(state => ({
   116      isConnected: state.autopilot.isConnected,
   117      signals: state.autopilot.signals,
   118      marketStatus: state.autopilot.marketStatus,
   119      stats: state.autopilot.stats,
   120      scanTick: state.autopilot.scanTick,
   121      lastAnalysis: state.autopilot.lastAnalysis,
   122      config: state.autopilot.config,
   123      strategies: state.autopilot.strategies,
   124    }))
   125  );
   126
   127  /**
   128   * Send a command to the autopilot backend.
   129   */
   130  const sendCommand = (command: AutopilotCommand) => {
   131    autopilotWebsocket.sendCommand(command);
   132  };
   133
   134  // Derived stats with lifetime P&L (realized from backend + unrealized from open positions)
   135  // Backend's totalPnlSol = realized P&L from closed trades
   136  // Frontend's unrealizedPnlSol = live P&L from open positions via NATS
   137  const derivedStats = useMemo(() => {
   138    const lifetimePnl = stats.totalPnlSol + unrealizedPnlSol;
   139    // DIAGNOSTIC: Log P&L calculation
   140    console.log("[Autopilot] P&L calc:", {
   141      backendRealizedPnl: stats.totalPnlSol,
   142      unrealizedPnlSol,
   143      lifetimePnl,
   144    });
   145    return {
   146      ...stats,
   147      totalPnlSol: lifetimePnl,
   148    };
   149  }, [stats, unrealizedPnlSol]);
   150
   151  // Derived market status with computed SOL price and change
   152  // Note: Shows "change since widget opened" not "24h change" (true 24h would need historical data)
   153  const derivedMarketStatus = useMemo(() => {
   154    return {
   155      ...marketStatus,
   156      solPriceUsd: solPriceUsd ?? 0,
   157      solPriceChangePercent,
   158    };
   159  }, [marketStatus, solPriceUsd, solPriceChangePercent]);
   160
   161  return {
   162    // Connection status
   163    isConnected,
   164
   165    // Positions with live P&L (calculated from NATS market data)
