  invested_total_usd: string;
  invested_total_sol: string;
  is_bot: boolean;
  is_dev: boolean;
  is_insider: boolean;
  is_sniper: boolean;
  is_bundler: boolean;
  is_hard_bundler: boolean;
  // An identifier for bundles to allow the grouping of bundlers into their bundles
  bundle_id: string | null;
  // Number of bundled buys the holder has taken part in. This includes the number of hard_bundled_buys below
  bundled_buys: number;
  // Number of bundled sells the holder has taken part in. This includes the number of hard_bundled_sells below
  bundled_sells: number;
  last_bundled_buy_slot: number | null;
  last_bundled_sell_slot: number | null;
  hard_bundled_buys: number;
  hard_bundled_sells: number;
