export interface BaseLockFields {
  cancelable_by_sender: boolean;
  cancelable_by_recipient: boolean;
  transferable_by_sender: boolean;
  transferable_by_recipient: boolean;
  last_withdrawn_at: number | null;
  sender: string;
  recipient: string;
}

export interface StreamFlowNameField {
  stream_name: string;
}

export interface JupiterNameField {
  name: string;
}

export interface SimpleLockFields {
  unlock_timestamp: number;
}

export interface VestingLockFields {
  start_time: number;
  cliff_time: number;
  cliff_amount: string;
  period: number;
  amount_per_period: string;
}

export interface JupiterVestingFields {
  number_of_periods: number;
}

export type SimpleLock<TNameField = StreamFlowNameField> = BaseLockFields &
  SimpleLockFields &
  TNameField;
export type VestingLock<TNameField = StreamFlowNameField> = BaseLockFields &
  VestingLockFields &
  TNameField;
export type JupiterVestingLock = VestingLock<JupiterNameField> &
  JupiterVestingFields;

export type StreamFlowSimpleLock = SimpleLock<StreamFlowNameField>;
export type StreamFlowVestingLock = VestingLock<StreamFlowNameField>;
export type JupiterSimpleLock = SimpleLock<JupiterNameField>;

export type LockType =
  | { StreamFlowSimple: StreamFlowSimpleLock }
  | { StreamFlowVesting: StreamFlowVestingLock }
  | { JupiterSimple: JupiterSimpleLock }
  | { JupiterVesting: JupiterVestingLock };

