};

export type NotificationContent =
  | ReferralAcceptedNotificationContent
  | LimitOrderNotificationContent
  | ReferralTierLevelChangeNotificationContent
  | RewardsNotificationContent;

export type NotificationMessage = {
  id: string;
  notificationType: NotificationType;
  content: NotificationContent;
  isRead: boolean;
  telemetryId?: string;
  userId?: string;
  createdAt: string;
};

