// Token Event related types
////////////////////////////////////////////////////////////

type EventTradePayload = {
  market_cap_sol: DecimalString;
  market_cap_usd: DecimalString;
  sol_amount: DecimalString;
  token_amount: DecimalString;
  usd_amount: DecimalString;
};

