  LpAdd?: EventLpAddOrLpRemove;
};

export type LpRemoveEventData = {
  LpRemove?: EventLpAddOrLpRemove;
};

export type EventTrade = {
  trade_payload: EventTradePayload;
};

export type EventLpAddOrLpRemove = {
  lp_payload?: EventLiquidityPayload;
};

export type EventLiquidityPayload = {
  exchange: ExchangeOrLaunchpad;
  pool_address: Pubkey;
  mint: Pubkey;
  quote: Pubkey;
  mint_amount: DecimalString;
  quote_amount: DecimalString;
  timestamp: UnixNanoTimestamp;
  lp_address: Pubkey | null;
  total_value_sol: string;
