};

export type TokenEventPayload =
  | { MintAuthorityDisabled: unknown }
  | { FreezeAuthorityDisabled: unknown }
  | { Burned: { burned_pct: string } }
  | { SupplyChanged: { new_supply: string } }
  | { MigrationStarted: unknown }
  | { MigrationCompleted: { new_pool: string; new_dex: string } }
  | { LpBurned: { burned_pct: string } }
  | { DevBought: EventTrade }
  | { DevSold: EventTrade }
  | { InsiderBought: EventTrade }
  | { InsiderSold: EventTrade }
  | { SniperBought: EventTrade }
  | { SniperSold: EventTrade }
  | LpAddEventData
  | LpRemoveEventData;

export type TokenEventMessage = {
  event: TokenEventPayload;
  signature: string;
  timestamp: UnixNanoTimestamp;
  slot: IntegerString;
};

