import type { SubStore } from "@/store/types";
import type { SolanaPriceStore } from "./types";
import type { GlobalStore } from "../../types";

export const getSolanaPriceStore: SubStore<
  SolanaPriceStore,
  GlobalStore
> = set => ({
  usd: null,
  slot: null,

  setPriceUsd: (price, slot) =>
    set(
      state => {
        state.solanaPrice.usd = price;
        state.solanaPrice.slot = slot;
      },
      false,
      {
        type: "setPriceUsd",
        data: { price, slot },
      }
    ),
});

